import { queryData, addData } from '@/api/common';
export default {
  data: function data() {
    return {
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 7
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 13
        }
      },
      visible: false,
      confirmLoading: false,
      form: this.$form.createForm(this),
      orgNames: [],
      roleNames: [],
      editData: {},
      org: 0,
      role: 0,
      isDel: 0
    };
  },
  created: function created() {
    this.handleRoleList();
    this.handleOrgList();
  },
  methods: {
