      var _this = this;

      var roleId = JSON.parse(localStorage.getItem('user')).roleId;
      queryData({
        role: roleId
      }, '/userManage/getAllSysRole').then(function (res) {
        console.log(res);
        _this.roleNames = res.result;

        if (res.code !== 0) {
          _this.$message.error(res.message);
        }
      });
    },
    // 获取单位下的单位
    handleOrgList: function handleOrgList() {
      var orgId = JSON.parse(localStorage.getItem('user')).orgId;
      var param = {
        org: orgId
      };

      var _this = this;

      queryData(param, '/userManage/getHrOrgByPid').then(function (res) {
        console.log(res);
        _this.orgNames = res.result;

        if (res.code !== 0) {
          _this.$message.error(res.message);
        }
      });
    },
    add: function add(record) {
      console.log(record);
      this.form.resetFields();
      this.org = record.org;
      this.role = record.role;
      this.isDel = record.isDel;
      this.visible = true;
      this.editData = record;
    },
    handleChange: function handleChange(value) {
      this.isDel = value;
    },
