import { queryData } from '@/api/common';
import moment from 'moment';
export default {
  data: function data() {
    return {
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 7
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 13
        }
      },
      visible: false,
      confirmLoading: false,
      form: this.$form.createForm(this),
      orgNames: [],
      editData: '',
      orgId: ''
    };
  },
  created: function created() {},
  methods: {
    moment: moment,
    // 获取该用户下的所属单位
    handleOrgLists: function handleOrgLists() {
      var _this = this;

      var orgId = JSON.parse(localStorage.getItem('user')).orgId;
      var param = {
        org: orgId
      };
      queryData(param, '/userManage/getHrOrgByPid').then(function (res) {
        console.log(res);
        _this.orgNames = res.result;

        if (res.code !== 0) {
          _this.$message.error('获取单位数据', res.message);
        }
      }).catch(function (err) {
        return console.log(err);
      });
    },
    add: function add(record) {
      this.form.resetFields();
      this.orgId = record.orgId;
      console.log('表单的值人脸时间应该' + this.editData.faceTime);
      this.editData = record;
      this.visible = true;
      this.handleOrgLists();
    },
    handleSubmit: function handleSubmit() {
      var _this2 = this;

      var validateFields = this.form.validateFields;
      this.confirmLoading = true;
      validateFields(function (errors, values) {
        if (!errors) {
          var param = {
            code: _this2.editData.code,
            name: values.name,
            validity: values.validity,
            faceTime: values.faceTime,
            orgId: _this2.orgId
          };
          var _this = _this2;
          setTimeout(function () {
            queryData(param, '/robotInfo/save').then(function (res) {
              console.log(res);

              if (res.code == 0) {
                _this.$message.success('修改成功');
              } else {
                _this.$message.error(res.message);
              } // 重置输入框内容


              _this.form.resetFields();
            });
            _this2.visible = false;
            _this2.confirmLoading = false;

            _this2.$emit('ok', values);
          }, 300);
        } else {
          _this2.confirmLoading = false;
        }
      });
    },
    handleCancel: function handleCancel() {
      this.visible = false;
      this.editData = {};
    },
    filterOption: function filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0;
    }
  }
};
