import { STable } from '@/components';
import { PageView } from '@/layouts';
import ShowMap from "./modules/ShowMap";
import EditMap from "./modules/EditMap";
import { queryData } from '@/api/common';
export default {
  name: 'TableList',
  components: {
    STable: STable,
    PageView: PageView,
    ShowMap: ShowMap,
    EditMap: EditMap
  },
  data: function data() {
    var _this2 = this;

    return {
      // 高级搜索 展开/关闭
      advanced: false,
      // 查询参数
      queryParam: {
        robotName: '',
        mapName: '',
        queryOrgId: ''
      },
      columns: [{
        title: '地图编码',
        dataIndex: 'id',
        scopedSlots: {
          customRender: 'id'
        }
      }, {
        title: '地图名称',
        dataIndex: 'mapName',
        scopedSlots: {
          customRender: 'mapName'
        }
      }, {
        title: '上传时间',
        dataIndex: 'updateDate',
        scopedSlots: {
          customRender: 'updateDate'
        }
      }, {
        title: '上传机器人',
        dataIndex: 'robotName',
        scopedSlots: {
          customRender: 'robotName'
        }
      }, {
        title: '所属单位',
        dataIndex: 'orgName',
        scopedSlots: {
          customRender: 'orgName'
        }
      }, {
        title: '操作',
        dataIndex: 'action',
        scopedSlots: {
          customRender: 'action'
        }
      }],
      // 加载数据方法 必须为 Promise 对象
      loadData: function loadData(parameter) {
        var userId = JSON.parse(localStorage.getItem('user')).id;
        var orgId = JSON.parse(localStorage.getItem('user')).orgId;
        return queryData(Object.assign(parameter, _this2.queryParam, {
          userId: userId,
          orgId: orgId
        }), '/robotMapLib/list').then(function (res) {
          console.log(res);
          return res.result;
        });
      },
      orgNames: []
    };
  },
  computed: {
    title: function title() {
      return this.$route.meta.title;
    }
  },
  created: function created() {
    this.handleOrgLists();
  },
  methods: {
    // 获取该用户下的所属单位
    handleOrgLists: function handleOrgLists() {
      var _this = this;

      var orgId = JSON.parse(localStorage.getItem('user')).orgId;
      var param = {
        org: orgId
      };
      queryData(param, '/userManage/getHrOrgByPid').then(function (res) {
        _this.orgNames = res.result;

        if (res.code !== 0) {
          _this.$message.error(res.message);
        }
      }).catch(function (err) {
        return console.log(err);
      });
    },
    filterOption: function filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0;
    },
    // 表单重置
    resetQuery: function resetQuery() {
      this.queryParam = {};
      this.$refs.table.refresh(true);
    },
    // 搜索内容
    searchData: function searchData() {
      this.$refs.table.refresh(true);
    },
    handleOk: function handleOk() {
      this.$refs.table.refresh(true);
    },
    handleEditVisible: function handleEditVisible(record) {
      this.$refs.editModal.add(record);
    },
    handleShowMap: function handleShowMap(record) {
      this.$refs.showMap.add(record);
    }
  }
};
