import { addData, queryData } from '@/api/common';
import CmdList from "../../Cmd";
import { uploadUrl } from '@/api/upload';
export default {
  components: {
    CmdList: CmdList
  },
  data: function data() {
    return {
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 7
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 13
        }
      },
      visible: false,
      confirmLoading: false,
      form: this.$form.createForm(this),
      previewVisible: false,
      previewImage: '',
      fileList: [],
      fileName: '',
      cmdList: [],
      photo: '',
      faceName: '',
      mapId: '',
      mapList: [],
      cmd: '',
      showEditCmd: false,
      url: ''
    };
  },
  created: function created() {
    this.url = uploadUrl + 'faceManage/faceUpload';
    this.getCmd();
    this.getMap();
  },
  methods: {
    changeCmd: function changeCmd(event) {
      this.cmd = event;
      this.showEditCmd = true;
    },
    // 修改命令集
    editCmd: function editCmd() {
      var formData = this.form.getFieldsValue();

      if (formData.name == undefined) {
        this.$message.error('请先输入人脸名称');
        return;
      }

      var mapId = formData.mapId == undefined ? 0 : formData.mapId;
      console.log(mapId);
      this.$refs.cmd.show('编辑人脸命令集', mapId, this.cmd);
    },
    // 新增命令集
    addCmd: function addCmd() {
      var formData = this.form.getFieldsValue();

      if (formData.name == undefined) {
        this.$message.error('请先输入人脸名称');
        return;
      }

      var mapId = formData.mapId == undefined ? 0 : formData.mapId;
      this.$refs.cmd.show('编辑人脸命令集', mapId, 0);
    },
    add: function add() {
      this.form.resetFields();
      this.visible = true;
      this.cmd = '';
      this.showEditCmd = false;
    },
    // showCmd(){
    //   let formData = this.form.getFieldsValue();
    //   if(formData.name == undefined){
    //     this.$message.error('请先输入人脸名称')
    //     return;
    //   }
    //   let mapId = formData.mapId == undefined ? 0 : formData.mapId
    //   this.$refs.cmd.show(formData.name,mapId,this.selectCmd)
    // },
    // 编辑命令集后，获取命令集ID
    getCmdId: function getCmdId(cmd) {
      this.getCmd();
      this.form.setFieldsValue({
        cmd: parseInt(cmd)
      });
    },
    getMap: function getMap() {
      var _this2 = this;

      var org = JSON.parse(localStorage.getItem('user')).orgId;
      queryData({
        orgId: org
      }, '/robotMapLib/getRobotMapLibList').then(function (res) {
        console.log('-----------getMap', res);
        _this2.mapList = res.result;
      });
    },
    getCmd: function getCmd() {
      var org = JSON.parse(localStorage.getItem('user')).orgId;

      var _this = this;

      queryData({
        orgId: org
      }, '/cmdCollection/getCmdListByOrg').then(function (res) {
        console.log(res);

        if (res.code == 0) {
          _this.cmdList = res.cmdList;
        } else {
          _this.$message.error(res.message);
        }
      });
    },
    handleCancel: function handleCancel() {
      this.previewVisible = false;
    },
    handlePreview: function handlePreview(file) {
      console.log(file);
      this.previewImage = file.url || file.thumbUrl;
      this.previewVisible = true;
    },
    handleChange: function handleChange(_ref) {
      var fileList = _ref.fileList;

      if (fileList.length > 0) {
        if (fileList[0].status == 'done') {
          var res = fileList[0].response;
          console.log(res);

          if (res.code == 0) {
            this.fileName = res.fileName;
            this.$message.success("".concat(res.fileName, " \u4E0A\u4F20\u6210\u529F"));
          } else {
            this.$message.error(res.message);
          }
        }
      }

      this.fileList = fileList;
    },
    // 下拉框
    filterOption: function filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0;
    },
    handleSubmit: function handleSubmit() {
      var _this3 = this;

      var validateFields = this.form.validateFields;
      this.confirmLoading = true;
      validateFields(function (errors, values) {
        if (!errors) {
          var _this = _this3;

          if (_this3.fileName == '') {
            _this3.$message.error('请上传人脸图片');

            return;
          }

          setTimeout(function () {
            var org = JSON.parse(localStorage.getItem('user')).orgId; // values.cmd = this.cmd == '' ? values.cmd : this.cmd

            addData(Object.assign(values, {
              org: org,
              photo: _this3.fileName
            }), '/faceManage/addFaceMsg').then(function (res) {
              if (res.code == 0) {
                _this.$message.success('新建成功');
              } else {
                _this.$message.error(res.message);
              }

              _this.fileList = [];
              _this.visible = false;
              _this.confirmLoading = false;

              _this.$emit('ok', values); // 重置输入框内容


              _this.form.resetFields();
            });
          }, 300);
        } else {
          _this3.confirmLoading = false;
        }
      });
    }
  }
};
