      advanced: false,
      // 查询参数
      queryParam: {
        name: '',
        typeId: ''
      },
      cmdTypeList: [],
      // 表头
      columns: [{
        title: '#',
        dataIndex: 'no',
        scopedSlots: {
          customRender: 'serial'
        }
      }, {
        title: '编号',
        dataIndex: 'id',
        scopedSlots: {
          customRender: 'id'
        }
      }, {
        title: '命令名称',
        dataIndex: 'name',
        scopedSlots: {
          customRender: 'name'
        }
      }, {
        title: '命令名称参数类型',
        dataIndex: 'typeName',
        scopedSlots: {
          customRender: 'typeName'
        }
      }, {
        title: '操作',
        dataIndex: 'action',
        scopedSlots: {
          customRender: 'action'
        }
      }],
      // 加载数据方法 必须为 Promise 对象
      loadData: function loadData(parameter) {
        return queryData(Object.assign(parameter, _this2.queryParam), '/cmdUnit/list').then(function (res) {
          console.log(res);
          return res.result;
        });
      }
    };
  },
  computed: {
    title: function title() {
      return this.$route.meta.title;
