import { STable } from '@/components';
import { PageView } from '@/layouts';
import EditRobot from "./modules/EditRobot";
import { queryData } from '@/api/common';
export default {
  name: 'TableList',
  components: {
    STable: STable,
    PageView: PageView,
    EditRobot: EditRobot
  },
  data: function data() {
    var _this2 = this;

    return {
      // 高级搜索 展开/关闭
      advanced: false,
      addVisible: false,
      editVisible: false,
      // orgId: this.$store.getters.orgId
      orgId: {
        orgId: 1
      },
      selectedItems: [],
      // 查询参数
      queryParam: {
        code: '',
        name: '',
        orgId: '',
        validityDate: ''
      },
      editData: {},
      options: {
        alert: false,
        rowSelection: null
      },
      // userId:localStorage.getItem('userId')
      // 表头
      columns: [{
        title: '#',
        dataIndex: 'no',
        scopedSlots: {
          customRender: 'serial'
        }
      }, {
        title: '机器人名称',
        dataIndex: 'name',
        scopedSlots: {
          customRender: 'name'
        }
      }, {
        title: '机器人编码',
        dataIndex: 'code',
        scopedSlots: {
          customRender: 'code'
        }
      }, {
        title: '单位名称',
        dataIndex: 'orgName',
        scopedSlots: {
          customRender: 'orgName'
        }
      }, {
        title: '地图名称',
        dataIndex: 'mapName',
        scopedSlots: {
          customRender: 'mapName'
        }
      }, {
        title: '有效截止时间',
        dataIndex: 'validityDate',
        scopedSlots: {
          customRender: 'validityDate'
        }
      }, {
        title: '人脸失效时间(分钟)',
        dataIndex: 'faceTime',
        scopedSlots: {
          customRender: 'faceTime'
        }
      }, {
        title: '操作',
        dataIndex: 'action',
        scopedSlots: {
          customRender: 'action'
        }
      }],
      // 加载数据方法 必须为 Promise 对象
      loadData: function loadData(parameter) {
        var userId = JSON.parse(localStorage.getItem('user')).id;
        var orgId = JSON.parse(localStorage.getItem('user')).orgId;
        return queryData(Object.assign(parameter, _this2.queryParam, {
          userId: userId,
          orgId: orgId
        }), '/robotInfo/list').then(function (res) {
          console.log(res);
          return res.result;
        });
      },
      orgNames: [],
      roleNames: [],
      selectedRowKeys: [],
      selectedRows: []
    };
  },
  created: function created() {
    this.handleOrgLists();
  },
  computed: {
    title: function title() {
      return this.$route.meta.title;
    }
  },
  methods: {
    // 获取该用户下的所属单位
    handleOrgLists: function handleOrgLists() {
      var _this = this;

      var id = JSON.parse(localStorage.getItem('user')).id;
      var param = {
        userId: id
      };
      queryData(param, '/robotInfo/getOrglist').then(function (res) {
        _this.orgNames = res.result;

        if (res.code !== 0) {
          _this.$message.error(res.message);
        }
      }).catch(function (err) {
        return console.log(err);
      });
    },
    onChange: function onChange(date, dateString) {
      this.queryParam.validityDate = dateString;
      console.log(date, dateString);
    },
    onSelectChange: function onSelectChange(selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys;
      this.selectedRows = selectedRows;
    },
    // 表单重置
    resetQuery: function resetQuery() {
      this.queryParam = {};
      this.$refs.table.refresh(true);
    },
    // 显示状态修改
    handleAddVisible: function handleAddVisible() {
      this.addVisible = true;
    },
    handleEditVisible: function handleEditVisible(record) {
      this.editData = record;
      console.log(record);
      this.$refs.editModal.add(record);
      this.editVisible = true;
    },
    filterOption: function filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0;
    },
    // 搜索内容
    searchData: function searchData() {
      this.$refs.table.refresh(true);
    },
    handleOk: function handleOk() {
      this.$refs.table.refresh(true);
    },
    // 删除
    handleDelete: function handleDelete(record) {
      var _this = this;

      this.$confirm({
        title: '警告',
        content: "\u771F\u7684\u8981\u5220\u9664 ".concat(record.name, " \u5417?"),
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk: function onOk() {
          console.log('OK'); // 在这里调用删除接口

          return new Promise(function (resolve, reject) {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500);
            var id = {
              entityId: record.id
            };
            queryData(id, '/robotInfo/delete').then(function (res) {
              console.log('删除返回值：', res);

              if (res.code == 0) {
                _this.$message.success('删除成功');

                _this.$refs.table.refresh(true);
              } else {
                _this.$message.error(res.message);
              }
            });
          }).catch(function () {
            return console.log('Oops errors!');
          });
        },
        onCancel: function onCancel() {
          console.log('Cancel');
        }
      });
    }
  }
};
