import { STable } from '@/components';
import EditModal from "./modules/EditRole";
import CreateModal from "./modules/CreateRole";
import { queryData } from '@/api/common';
export default {
  name: 'TableList',
  components: {
    STable: STable,
    EditModal: EditModal,
    CreateModal: CreateModal
  },
  data: function data() {
    var _this2 = this;

    return {
      roleId: 0,
      roleName: '',
      mdl: {},
      // 查询参数
      queryParam: {
        name: ''
      },
      // 表头
      columns: [{
        title: '唯一识别码',
        dataIndex: 'id'
      }, {
        title: '角色名称',
        dataIndex: 'name'
      }, {
        title: '角色等级',
        dataIndex: 'level'
      }, {
        title: '描述',
        dataIndex: 'describe'
      }, {
        title: '操作',
        width: '150px',
        dataIndex: 'action',
        scopedSlots: {
          customRender: 'action'
        }
      }],
      loadData: function loadData(parameter) {
        return queryData(Object.assign(parameter, _this2.queryParam), '/SysRoleManage/querySysRoleMsg').then(function (res) {
          return res.result;
        });
      }
    };
  },
  methods: {
    handleEdit: function handleEdit(record) {
      this.mdl = Object.assign({}, record);
      this.mdl.permissions.forEach(function (permission) {
        permission.actionsOptions = permission.actionEntitySet.map(function (action) {
          return {
            label: action.describe,
            value: action.action,
            defaultCheck: action.defaultCheck
          };
        });
      });
      this.roleName = record.name;
      console.log('当前选择', record.name);
      this.$refs.editModal.edit(record, record.name);
    },
    handleOk: function handleOk() {
      // 新增/修改 成功时，重载列表
      this.$refs.table.refresh();
    },
    // 处理重置
    handleReset: function handleReset() {
      this.queryParam.name = '';
      this.$refs.table.refresh();
    },
    // 删除
    handleDel: function handleDel(record) {
      var _this = this;

      this.$confirm({
        title: '警告',
        content: "\u771F\u7684\u8981\u5220\u9664 ".concat(record.name, " \u5417?"),
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk: function onOk() {
          console.log('OK'); // 在这里调用删除接口

          return new Promise(function (resolve, reject) {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 300);
            var id = {
              id: [record.id]
            };
            queryData(id, '/SysRoleManage/delSysRoleMsg').then(function (res) {
              if (res.code == 0) {
                _this.$message.success('删除成功');

                _this.$refs.table.refresh(true);
              } else {
                _this.$message.error(res.message);
              }
            });
          }).catch(function () {
            return console.log('Oops errors!');
          });
        },
        onCancel: function onCancel() {
          console.log('Cancel');
        }
      });
    }
  }
};
