import { getOrgList, getRoleList, editUser } from '@/api/manage';
export default {
  props: {
    editData: {
      type: Object,
      required: true
    }
  },
  data: function data() {
    return {
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 7
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 13
        }
      },
      visible: false,
      confirmLoading: false,
      form: this.$form.createForm(this),
      orgNames: [],
      roleNames: []
    };
  },
  created: function created() {
    this.handleRoleList();
    this.handleOrgList();
  },
  methods: {
    // 获取角色
    handleRoleList: function handleRoleList() {
      var _this = this;

      getRoleList().then(function (res) {
        console.log(res);
        _this.roleNames = res.result;

        if (res.code !== 0) {
          _this.$message.error(res.message);
        }
      });
    },
    // 获取单位下的单位
    handleOrgList: function handleOrgList() {
      var param = {
        org: 1
      };

      var _this = this;

      getOrgList(param).then(function (res) {
        console.log(res);
        _this.orgNames = res.result;

        if (res.code !== 0) {
          _this.$message.error(res.message);
        }
      });
    },
    add: function add() {
      this.visible = true;
    },
    handleSubmit: function handleSubmit() {
      var _this2 = this;

      var validateFields = this.form.validateFields;
      this.confirmLoading = true;
      validateFields(function (errors, values) {
        if (!errors) {
          var param = Object.assign(values, {
            id: _this2.editData.id
          });
          var _this = _this2;
          setTimeout(function () {
            editUser(param).then(function (res) {
              if (res.code == 0) {
                _this.$message.success('修改成功'); // 重置输入框内容


                _this.form.resetFields();
              } else {
                _this.$message.error(res.message);
              }
            });
            _this2.visible = false;
            _this2.confirmLoading = false;

            _this2.$emit('ok', values);
          }, 300);
        } else {
          _this2.confirmLoading = false;
        }
      });
    },
    handleCancel: function handleCancel() {
      this.visible = false;
    },
    handleChangeOption: function handleChangeOption(value) {
      console.log("select ".concat(value));
    },
    handleBlur: function handleBlur() {
      console.log('blur');
    },
    handleFocus: function handleFocus() {
      console.log('focus');
    },
    filterOption: function filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0;
    }
  }
};
