import { queryData, addData } from '@/api/common';
export default {
  data: function data() {
    return {
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 7
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 13
        }
      },
      visible: false,
      confirmLoading: false,
      form: this.$form.createForm(this),
      orgNames: [],
      roleNames: []
    };
  },
  created: function created() {
    this.handleRoleList();
    this.handleOrgList();
  },
  methods: {
    // 获取角色
    handleRoleList: function handleRoleList() {
      var _this = this;

      var roleId = JSON.parse(localStorage.getItem('user')).roleId;
      queryData({
        role: roleId
      }, '/userManage/getAllSysRole').then(function (res) {
        console.log('角色列表', res);
        _this.roleNames = res.result;

        if (res.code !== 0) {
          _this.$message.error(res.message);
        }
      });
    },
    // 获取单位下的单位
    handleOrgList: function handleOrgList() {
      var id = JSON.parse(localStorage.getItem("user")).orgId;
      var param = {
        org: id
      };

      var _this = this;

      queryData(param, '/userManage/getHrOrgByPid').then(function (res) {
        _this.orgNames = res.result;

        if (res.code !== 0) {
          _this.$message.error(res.message);
        }
      });
    },
    add: function add() {
      this.visible = true;
    },
    handleSubmit: function handleSubmit() {
      var _this2 = this;

      var validateFields = this.form.validateFields;
      this.confirmLoading = true;
      validateFields(function (errors, values) {
        if (values.mobile && parseInt(values.mobile).toString().length !== 11) {
          _this2.$message.error('请输入正确的手机号');

          return false;
        }

        if (!errors) {
          console.log('values', values);
          var _this = _this2;
          setTimeout(function () {
            addData(values, '/userManage/addUserMsg').then(function (res) {
              if (res.code == 0) {
                _this.$message.success('新建成功'); // 重置输入框内容


                _this.form.resetFields();

                _this2.visible = false;
                _this2.confirmLoading = false;

                _this2.$emit('ok', values);
              } else {
                _this.$message.error(res.message);
              }
            });
          }, 300);
        } else {
          _this2.confirmLoading = false;
        }
      });
    },
    handleCancel: function handleCancel() {
      this.visible = false;
    },
    filterOption: function filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0;
    }
  }
};
