import { STable } from '@/components';
import { PageView } from '@/layouts';
import EditRobot from "./modules/EditRobot";
import { queryData } from '@/api/common';
import addSkillRobot from "./modules/AddSkillRobot";
import ShowType from "./modules/showSkillsType";
import addAiuiScene from "./modules/AddAiuiScene";
export default {
  name: 'TableList',
  components: {
    STable: STable,
    PageView: PageView,
    EditRobot: EditRobot,
    addSkillRobot: addSkillRobot,
    ShowType: ShowType,
    addAiuiScene: addAiuiScene
  },
  data: function data() {
    var _this2 = this;

    return {
      // 高级搜索 展开/关闭
      advanced: false,
      addVisible: false,
      editVisible: false,
      orgId: {
        orgId: 1
      },
      selectedItems: [],
      // 查询参数
      queryParam: {
        code: '',
        name: '',
        orgId: '',
        validityDate: ''
      },
      editData: {},
      options: {
        alert: false,
        rowSelection: null
      },
      // 表头
      columns: [{
        title: '#',
        dataIndex: 'no',
        scopedSlots: {
          customRender: 'serial'
        }
      }, {
        title: '名称',
        dataIndex: 'name'
      }, {
        title: '编码',
        dataIndex: 'code'
      }, {
        title: '单位名称',
        dataIndex: 'orgName'
      }, {
        title: '地图名称',
        dataIndex: 'mapName'
      }, {
        title: 'aiui场景',
        dataIndex: 'aiuiScene'
      }, {
        title: '操作',
        dataIndex: 'action',
        scopedSlots: {
          customRender: 'action'
        }
      }],
      // 加载数据方法 必须为 Promise 对象
      loadData: function loadData(parameter) {
        var userId = JSON.parse(localStorage.getItem('user')).id;
        var orgId;

        if (_this2.queryParam.orgId > 0) {
          orgId = _this2.queryParam.orgId;
        } else {
          orgId = JSON.parse(localStorage.getItem('user')).orgId;
        }

        return queryData(Object.assign(parameter, _this2.queryParam, {
          userId: userId,
          orgId: orgId
        }), '/robotInfo/list').then(function (res) {
          console.log(res);
          return res.result;
        });
      },
      orgNames: [],
      roleNames: [],
      selectedRowKeys: []
    };
  },
  created: function created() {
    this.handleOrgLists();
  },
  computed: {
    title: function title() {
      return this.$route.meta.title;
    }
  },
  methods: {
    // 获取该用户下的所属单位
    handleOrgLists: function handleOrgLists() {
      var _this = this;

      var orgId = JSON.parse(localStorage.getItem('user')).orgId;
      var param = {
        org: orgId
      };
      queryData(param, '/userManage/getHrOrgByPid').then(function (res) {
        _this.orgNames = res.result;

        if (res.code !== 0) {
          _this.$message.error(res.message);
        }
      }).catch(function (err) {
        return console.log(err);
      });
    },
    onChange: function onChange(date, dateString) {
      this.queryParam.validityDate = dateString;
      console.log(date, dateString);
    },
    onSelectChange: function onSelectChange(selectedRowKeys) {
      this.selectedRowKeys = selectedRowKeys;
    },
    // 表单重置
    resetQuery: function resetQuery() {
      this.queryParam = {};
      this.$refs.table.refresh(true);
    },
    // 查看问答库
    selectSkillsType: function selectSkillsType(record) {
      this.$refs.showSkillsType.add(record);
    },
    // 查看问答库
    openSkillsRobot: function openSkillsRobot(record) {
      this.$refs.addSkillRobot.show(record);
    },
    // 查看问答库
    openAiui: function openAiui(record) {
      this.$refs.addAiuiScene.show(record);
    },
    filterOption: function filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0;
    },
    // 搜索内容
    searchData: function searchData() {
      this.$refs.table.refresh(true);
    },
    handleOk: function handleOk() {
      this.$refs.table.refresh(true);
    },
    // 删除机器人
    deleteAiui: function deleteAiui(record) {
      var _this = this;

      this.$confirm({
        title: '警告',
        content: "\u673A\u5668\u4EBA <".concat(record.name, ">\u7684aiui\u914D\u7F6E\u5C06\u88AB\u79FB\u9664\uFF0C\u786E\u5B9A\u5417\uFF1F"),
        okText: '确定',
        okType: 'danger',
        cancelText: '取消',
        onOk: function onOk() {
          // 在这里调用删除接口
          return new Promise(function (resolve, reject) {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500);
            queryData({
              robotCode: record.code
            }, '/aiuiRobot/deleteAiuiRobot').then(function (res) {
              if (res.code == 0) {
                _this.$message.success('删除成功');

                _this.$refs.table.refresh(true);
              } else {
                _this.$message.error(res.message);
              }
            });
          }).catch(function () {
            return console.log('Oops errors!');
          });
        },
        onCancel: function onCancel() {
          console.log('Cancel');
        }
      });
    }
  }
};
