import { queryData } from '@/api/common';
import CmdList from "../../Cmd";
import moment from 'moment';
var week = ['1', '2', '3', '4', '5', '6', '7'];
export default {
  components: {
    CmdList: CmdList
  },
  data: function data() {
    return {
      headers: {
        authorization: 'authorization-text'
      },
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 7
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 13
        }
      },
      visible: false,
      confirmLoading: false,
      form: this.$form.createForm(this),
      editData: '',
      week: week,
      showTimePoint: false,
      showWeek: false,
      showTime: false,
      timePoint: '',
      timePointMoment: '',
      time: '',
      timeMoment: '',
      weekList: [],
      // 日期集合
      cmdList: [],
      // 命令集集合
      mapList: [],
      // 地图集合
      showEditCmd: true,
      cmd: ''
    };
  },
  created: function created() {
    this.getCmd();
    this.getMap();
  },
  methods: {
    // 控制显示内容
    display: function display(value) {
      if (value === 1) {
        this.showTime = true;
        this.showTimePoint = false;
        this.showWeek = false;
      }

      if (value === 2) {
        this.showTime = true;
        this.showTimePoint = false;
        this.showWeek = true;
      }

      if (value === 3) {
        this.showTime = true;
        this.showTimePoint = true;
        this.showWeek = false;
      }
    },
    changeCmd: function changeCmd(event) {
      this.cmd = event;
      this.showEditCmd = true;
    },
    getCmdId: function getCmdId(cmd) {
      console.log(cmd);
      this.getCmd();
      this.form.setFieldsValue({
        cmd: parseInt(cmd)
      });
    },
    // 修改动作集
    editCmd: function editCmd() {
      var formData = this.form.getFieldsValue();
      var mapId = formData.mapId === undefined ? 0 : formData.mapId;
      this.$refs.cmd.show('编辑动作集', mapId, this.cmd);
    },
    // 新增动作集
    addCmd: function addCmd() {
      var formData = this.form.getFieldsValue();
      var mapId = formData.mapId === undefined ? 0 : formData.mapId;
      this.$refs.cmd.show('新增动作集', mapId, 0);
    },
    getMap: function getMap() {
      var _this2 = this;

      var org = JSON.parse(localStorage.getItem('user')).orgId;
      queryData({
        orgId: org
      }, '/robotMapLib/getRobotMapLibList').then(function (res) {
        console.log('-----------getMap', res);
        _this2.mapList = res.result;
      });
    },
    getCmd: function getCmd() {
      var org = JSON.parse(localStorage.getItem('user')).orgId;

      var _this = this;

      queryData({
        orgId: org
      }, '/cmdCollection/getCmdListByOrg').then(function (res) {
        console.log(res);

        if (res.code === 0) {
          _this.cmdList = res.cmdList;
        } else {
          _this.$message.error(res.message);
        }
      });
    },
    timePointStr: function timePointStr(dates, dateStrings) {
      this.timePoint = dateStrings;
    },
    timeStr: function timeStr(dates, dateStrings) {
      this.time = dateStrings;
    },
    // selectWeek (value) {
    //   this.weekList = value
    //   console.log(this.weekList)
    // },
    edit: function edit(record) {
      console.log(record);
      this.form.resetFields();
      this.editData = record;
      this.cmd = record.cmdId;
      this.timeMoment = moment(record.times, 'HH:mm');
      this.time = record.times;
      this.visible = true;

      if (record.type === 1) {
        this.showTime = true;
        this.showTimePoint = false;
        this.showWeek = false;
      }

      if (record.type === 2) {
        this.showTime = true;
        this.showTimePoint = false;
        this.showWeek = true;
      }

      if (record.type === 3) {
        this.timePointMoment = moment(record.timePoint, 'YYYY-MM-DD');
        this.timePoint = record.timePoint;
        this.showTime = true;
        this.showTimePoint = true;
        this.showWeek = false;
      }
    },
    handleSubmit: function handleSubmit() {
      var _this3 = this;

      var validateFields = this.form.validateFields;
      this.confirmLoading = true;
      validateFields(function (errors, values) {
        if (!errors) {
          values.time = _this3.time;
          values.timePoint = _this3.timePoint;
          var _this = _this3;
          setTimeout(function () {
            var param = Object.assign(values, {
              id: _this3.editData.id
            });
            queryData(param, '/timedTask/editTaskMsg').then(function (res) {
              if (res.code === 0) {
                _this.$message.success('修改成功');
              } else {
                _this.$message.error(res.message);
              }

              _this.visible = false;
              _this.confirmLoading = false;

              _this.$emit('ok', values); // 重置输入框内容


              _this.form.resetFields();
            });
          }, 300);
        } else {
          _this3.confirmLoading = false;
        }
      });
    },
    handleCancel: function handleCancel() {
      this.visible = false;
    }
  }
};
