import { addData, queryData } from '@/api/common';
import { STable } from '@/components';
export default {
  name: 'TableList',
  components: {
    STable: STable
  },
  data: function data() {
    return {
      confirmLoading: false,
      form: this.$form.createForm(this),
      showIframe: false,
      cmdSrc: 'http://localhost:8000/robot.html'
    };
  },
  methods: {
    show: function show(title, mapId) {
      var user = JSON.parse(localStorage.getItem('user'));
      var param = '?orgId=' + user.orgId + '&createBy=' + user.id + '&circulation=1' + '&mapId=' + mapId + '&title=' + title;
      console.log('--------------cmdSrc', this.cmdSrc + param);
      this.cmdSrc = this.cmdSrc + param;
      this.showIframe = true;
    },
    handleSubmit: function handleSubmit() {
      var cmdStr = this.$refs.iframe.contentWindow.document.getElementById("number");
      var cmdId = cmdStr.innerHTML;

      if (cmdId == 0) {
        this.$message.error('请先点击左下角保存按钮');
      }

      this.$emit('getCmdId', cmdId);
      console.log('--------------命令集id：', cmdId);
    },
    filterOption: function filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0;
    }
  }
};
