import { queryData } from '@/api/common';
import CmdList from "../../Cmd";
export default {
  components: {
    CmdList: CmdList
  },
  data: function data() {
    return {
      headers: {
        authorization: 'authorization-text'
      },
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 7
        }
      },
      wrapperCol: {
