      cmdList: [],
      visible: false,
      form: this.$form.createForm(this),
      editData: {},
      // isAble: false,
      mapList: []
    };
  },
  created: function created() {
    this.getMap();
  },
  methods: {
    changeMap: function changeMap(event) {
      console.log('选择项: ' + event);
      this.editData.mapId = event;
    },
    add: function add(record) {
      console.log(record);
      this.visible = true;
      this.editData = record; // this.isAble = record.isAble == 1 ? true : false

      this.entityId = record.id;
    },
    getMap: function getMap() {
      var _this2 = this;

      var org = JSON.parse(localStorage.getItem('user')).orgId;
      queryData({
        orgId: org
      }, '/robotMapLib/getRobotMapLibList').then(function (res) {
        console.log('-----------getMap', res);
        _this2.mapList = res.result;
      });
    },
    getCmdId: function getCmdId(cmd) {
      this.editData.cmd = parseInt(cmd);
      console.log('getCmdId_____________+', cmd, this.editData.cmd);
    },
    showCmd: function showCmd() {
      var mapId = this.editData.mapId == undefined ? 0 : this.editData.mapId;
      console.log(mapId);
      var cmd = this.editData.id == undefined ? 0 : this.editData.id;
      this.$refs.cmd.show(this.editData.name, mapId, cmd);
    },
    handleSubmit: function handleSubmit() {
      var _this3 = this;

      var validateFields = this.form.validateFields;
      this.confirmLoading = true;
      validateFields(function (errors, values) {
        if (!errors) {
          var _this = _this3;
          var user = JSON.parse(localStorage.getItem('user'));
          var orgId = user.orgId;
          var userId = user.id;
          var mapId = _this3.editData.mapId;
          var title = _this3.editData.title;
          var circulation = _this3.editData.circulation;
          var param = {
            orgId: orgId,
            createBy: userId,
            entityId: _this3.editData.id,
            mapId: mapId,
            title: title,
            circulation: circulation
          };
          console.info(param);
          console.log(values);
          queryData(Object.assign(param, values), '/cmdCollection/saveOrUpdate').then(function (res) {
            console.log(res);

            if (res.code == 0) {
              _this3.$message.success('编辑成功');

              _this.visible = false;

              _this.$emit('ok', values);
            } else {
              _this.$message.error(res.message);
            }
          });
        }
      });
    },
    filterOption: function filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0;
    }
  }
};
