import { queryData } from '@/api/common';
export default {
  props: ['skillsId', 'skillTableRefresh'],
  data: function data() {
    return {
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 7
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 13
        }
      },
      visible: false,
      skillList: [],
      selectSkills: []
    };
  },
  methods: {
    // 显示技能
    getSkillList: function getSkillList() {
      var _this = this;

      var param = {
        robotSkillTypeId: '5',
        skillsId: this.skillsId
      };
      queryData(param, '/robotSkill/getRobotSkillByTypeList').then(function (res) {
        console.log(res.result);
        _this.skillList = res.result;
        return res.result;
      });
    },
    add: function add() {
      this.visible = true;
      this.getSkillList();
    },
    handleChange: function handleChange(value) {
      console.log("Selected: ".concat(value));
      this.selectSkills = value;
    },
    afterClose: function afterClose() {},
    handleSubmit: function handleSubmit() {
      var _this = this;

      if (this.selectSkills.length <= 0) {
        _this.$message.success('未添加技能');

        _this.visible = false;
        return;
      }

      var orgId = JSON.parse(localStorage.getItem('user')).orgId;
      var skills = this.selectSkills.map(function (value, index) {
        return _this.skillList[value];
      });
      var param = {
        user: orgId,
        skillsId: this.skillsId,
        robotSkillList: skills
      };
      queryData(param, '/skillsOption/saveList').then(function (res) {
        console.log(res.result);

        if (res.code == 0) {
          _this.$message.success('添加成功');

          _this.selectSkills = [];
          _this.visible = false;

          _this.skillTableRefresh();
        } else {
          _this.$message.error(res.message);
        }
      });
    }
  }
};
