// eslint-disable-next-line
import { UserLayout, BasicLayout, RouteView, BlankLayout, PageView } from '@/layouts'
import { bxAnaalyse } from '@/core/icons'

import { getMenu } from '@/api/manage'

export const asyncRouterMap = [
  {
    path: '/',
    name: 'index',
    component: BasicLayout,
    meta: { title: '首页' },
    redirect: '/dashboard/workplace',
    children: [
      // dashboard
      {
        path: '/dashboard',
        name: 'dashboard',
        redirect: '/dashboard/workplace',
        component: RouteView,
        meta: { title: '首页', keepAlive: true, icon: bxAnaalyse },
        children: [
          {
            path: '/dashboard/workplace',
            name: 'Workplace',
            component: () => import('@/views/dashboard/Workplace'),
            meta: { title: '工作台', keepAlive: true, }
          }
        ]
      },
       // system
       {
        path: '/system',
        name: 'system',
        component: RouteView,
        redirect: '/system/Menu',
        meta: { title: '系统管理', icon: 'setting', permission: ['role','user'] },
        children: [
          {
            path: '/system/Role',
            name: 'systemRole',
            component: () => import('@/views/system/Role'),
            meta: { title: '角色库' ,keepAlive: true, permission: ['role'] }
          },
          {
            path: '/system/User',
            name: 'systemUser',
            component: () => import('@/views/system/User'),
            meta: { title: '管理员库' , keepAlive: true,permission: ['user' ] }
          },
          {
            path: '/system/PhoneApp',
            name: 'systemPhoneApp',
            component: () => import('@/views/system/PhoneApp'),
            meta: { title: '手机app管理',  keepAlive: false, permission: [ 'role' ]  }
          },
        ]
      },
       // Company
       {
        path: '/company',
        name: 'company',
        component: RouteView,
        redirect: '/company/ComList',
        meta: { title: '单位管理', icon: 'bank', permission: [ 'company' ] },
        children: [
          {
            path: '/company/ComList',
            name: 'ComList',
            component: () => import('@/views/company/ComList'),
            meta: { title: '单位列表' , permission: [ 'company' ] }
          },
        ]
      },
      // Company
      {
        path: '/robot',
        name: 'Robot',
        component: RouteView,
        redirect: '/robot/RobotList',
        meta: { title: '机器人管理', icon: 'android', permission: [ 'robot' ] },
        children: [
          {
            path: '/robot/List',
            name: 'RobotList',
            component: () => import('@/views/robot/RobotList'),
            meta: { title: '机器人列表' , permission: [ 'robot' ] }
          },
          {
            path: '/robot/Map',
            name: 'MapList',
            component: () => import('@/views/robot/MapList'),
            meta: { title: '地图管理' , permission: [ 'robot' ] }
          },
        ]
      },
      // Company
      {
        path: '/instruction',
        name: 'instruction',
        component: RouteView,
        redirect: '/instruction/Expression',
        meta: { title: '指令集库', icon: 'inbox',keepAlive: false, permission: [ 'robot' ] },
        children: [
          {
            path: '/instruction/Expression',
            name: 'ExpressionList',
            component: () => import('@/views/instruction/Expression'),
            meta: { title: '表情管理' , permission: [ 'robot' ] }
          },
          // {
          //   path: '/instruction/SuperCmd',
          //   name: 'SuperCmdList',
          //   component: () => import('@/views/instruction/SuperCmd'),
          //   meta: { title: '超级指令' , permission: [ 'robot' ] }
          // },
          {
            path: '/instruction/TouchCmd',
            name: 'TouchCmdList',
            component: () => import('@/views/instruction/TouchCmd'),
            meta: { title: '触发指令' , permission: [ 'robot' ] }
          },
          {
            path: '/instruction/CellCmd',
            name: 'CellCmdList',
            component: () => import('@/views/instruction/CellCmd'),
            meta: { title: '单元命令' ,keepAlive: false, permission: [ 'robot' ] }
          },
          {
            path: '/instruction/CmdType',
            name: 'CmdTypeList',
            component: () => import('@/views/instruction/CmdType'),
            meta: { title: '单元命令类型' ,keepAlive: false, permission: [ 'robot' ] }
          },
          {
            path: '/instruction/Sensor',
            name: 'SensorList',
            component: () => import('@/views/instruction/Sensor'),
            meta: { title: '传感器' , permission: [ 'robot' ] }
          },
        ]
      },
      // face
      {
        path: '/face',
        name: 'face',
        component: RouteView,
        redirect: '/face/FaceList',
        meta: { title: '人脸管理', icon: 'smile', permission: [ 'face' ] },
        children: [
          {
            path: '/face/FaceList',
            name: 'faceList',
            component: () => import('@/views/face/FaceList'),
            meta: { title: '人脸库' , permission: [ 'face' ] }
          }
        ]
      },
      // ques
      {
        path: '/ques',
        name: 'ques',
        component: RouteView,
        redirect: '/ques/Business',
        meta: { title: '问答库管理', icon: 'notification', permission: [ 'quesBusiness','quesChat','quesOldData' ] },
        children: [
          {
            path: '/ques/Business',
            name: 'quesBusiness',
            component: () => import( '@/views/ques/Business'),
            meta: { title: '业务', permission: [ 'quesBusiness' ]  }
          },
          {
            path: '/ques/Chat',
            name: 'quesChat',
            component: () => import( '@/views/ques/Chat'),
            meta: { title: '闲聊' , permission: [ 'quesChat' ]}
          },
          {
            path: '/ques/OldData',
            name: 'quesOldData',
            component: () => import( '@/views/ques/OldData'),
            meta: { title: '老数据', permission: [ 'quesOldData' ] }
          }
        ]
      },
      


    ]
  },
  
  {
    path: '*', redirect: '/404', hidden: true
  }
]

/**
 * 基础路由
 * @type { *[] }
 */
export const constantRouterMap = [
  {
    path: '/user',
    component: UserLayout,
    redirect: '/user/login',
    hidden: true,
    children: [
      {
        path: 'login',
        name: 'login',
        component: () => import(/* webpackChunkName: "user" */ '@/views/user/Login')
      },
      {
        path: 'register',
        name: 'register',
        component: () => import(/* webpackChunkName: "user" */ '@/views/user/Register')
      },
      {
        path: 'register-result',
        name: 'registerResult',
        component: () => import(/* webpackChunkName: "user" */ '@/views/user/RegisterResult')
      }
    ]
  },

  {
    path: '/test',
    component: BlankLayout,
    redirect: '/test/home',
    children: [
      {
        path: 'home',
        name: 'TestHome',
        component: () => import('@/views/Home')
      }
    ]
  },

  {
    path: '/404',
    component: () => import(/* webpackChunkName: "fail" */ '@/views/exception/404')
  }

]
