import { STable } from '@/components';
export default {
  name: 'TableList',
  components: {
    STable: STable
  },
  data: function data() {
    return {
      confirmLoading: false,
      form: this.$form.createForm(this),
      showIframe: false,
      // cmdSrc: 'http://robot.lovechuangchuang.com:8084/robot.html'
      cmdSrc: 'http://192.168.1.110:9000/robot.html' // cmdSrc: 'http://localhost:8000/robot.html'

    };
