      this.cmdSrc = this.cmdSrc + encodeURI(param);
      console.log(this.cmdSrc);
      this.showIframe = true;
    },
    //右下角确定事件
    handleSubmit: function handleSubmit() {
      var cmd = this.$refs.iframe.contentWindow.document.getElementById('cmd').innerHTML;
      console.log(this.$refs.iframe.contentWindow.document.getElementById('cmd')); // let cmdName = this.$refs.iframe.contentWindow.document.getElementById('cmdName').innerHTML

      console.log('----------确定以后的值，待接收' + cmd);

      if (cmd == 0) {
        this.$message.error('请先点击保存');
      } else {
        this.$emit('getCmdId', cmd);
        this.showIframe = false;
      }
    }
  }
};
