  name: 'TableList',
  components: {
    Ellipsis: Ellipsis,
    STable: STable,
    PageView: PageView,
    EditForm: EditForm,
    CreateForm: CreateForm
  },
  data: function data() {
    var _this2 = this;

    return {
      headers: {
        authorization: 'authorization-text'
