      url: uploadUrl,
      // 查询参数
      queryParam: {
        name: '',
        navigateType: '',
        androidType: ''
      },
      editData: {},
      options: {
        alert: false,
        rowSelection: null
      },
      // 表头
      columns: [{
        title: '#',
        dataIndex: 'no',
        scopedSlots: {
          customRender: 'serial'
        }
      }, {
        title: '机器人编码',
        dataIndex: 'robotCode'
      }, {
        title: '定时任务描述',
        dataIndex: 'taskDesc'
      }, {
        title: '执行命令集',
        dataIndex: 'cmdName'
      }, {
        title: '备注',
        dataIndex: 'remark',
        scopedSlots: {
          customRender: 'remark'
        }
      }, {
        title: '创建时间',
        dataIndex: 'createDate'
      }, {
        title: '更新时间',
        dataIndex: 'updateDate'
      }, {
        title: '单位',
        dataIndex: 'orgName'
      }, {
        title: '操作',
        dataIndex: 'action',
        scopedSlots: {
          customRender: 'action'
        }
      }],
      // 加载数据方法 必须为 Promise 对象
      loadData: function loadData(parameter) {
        console.log('App 请求', parameter);
        return queryData(Object.assign(parameter, _this2.queryParam), '/timedTask/queryTaskMsg').then(function (res) {
          console.log(res);
          return res.result;
        });
      }
    };
  },
  computed: {
    title: function title() {
      return this.$route.meta.title;
    }
