      var _this = this;

      this.$confirm({
        title: '警告',
        content: "\u771F\u7684\u8981\u5220\u9664\u5B9A\u65F6\u4EFB\u52A1\u5417?",
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk: function onOk() {
          console.log('OK'); // 在这里调用删除接口

          return new Promise(function (resolve, reject) {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500);
            var param = {
              id: record.id,
              robotCode: record.robotCode
            };
            queryData(param, '/timedTask/delTaskMsg').then(function (res) {
              console.log('删除返回值：', res);

              if (res.code == 0) {
                _this.$message.success('删除成功');

                _this.$refs.table.refresh();
              } else {
                _this.$message.error(res.message);
              }
            });
          }).catch(function () {
            return console.log('Oops errors!');
          });
        },
        onCancel: function onCancel() {
          console.log('Cancel');
        }
      });
    }
  }
};
