import { STable } from '@/components';
import { PageView } from '@/layouts';
import CmdList from "../Cmd";
import EditCmdColl from "./modules/EditCmdColl"; // import EditRobot from './modules/EditRobot'

import { queryData } from '@/api/common';
import CreateCmdColl from "./modules/CreateCmdColl";
export default {
  name: 'TableList',
  components: {
    CreateCmdColl: CreateCmdColl,
    STable: STable,
    PageView: PageView,
    EditCmdColl: EditCmdColl,
    // EditRobot,
    CmdList: CmdList
  },
  data: function data() {
    var _this2 = this;

    return {
      advanced: false,
      addVisible: false,
      editVisible: false,
      // orgId: this.$store.getters.orgId
      orgId: {
        orgId: 1
      },
      selectedItems: [],
      // 查询参数
      queryParam: {
        name: '',
        orgId: ''
      },
      //   editData:{},
      cmdpage: {
        alert: false,
        rowSelection: null
      },
      // userId:localStorage.getItem('userId')
      // 表头
      columns: [{
        title: '#',
        dataIndex: 'no',
        scopedSlots: {
          customRender: 'serial'
        }
      }, {
        title: '名称',
        dataIndex: 'name',
        scopedSlots: {
          customRender: 'name'
        }
      }, {
        title: '命令集ID',
        dataIndex: 'id',
        scopedSlots: {
          customRender: 'id'
        }
      }, {
        title: '循环次数',
        dataIndex: 'circulation',
        scopedSlots: {
          customRender: 'circulation'
        }
      }, {
        title: '操作时间',
        dataIndex: 'create_date',
        scopedSlots: {
          customRender: 'create_date'
        }
      }, {
        title: '操作',
        dataIndex: 'action',
        scopedSlots: {
          customRender: 'action'
        }
      }],
      // 加载数据方法 必须为 Promise 对象
      loadData: function loadData(parameter) {
        var orgId = JSON.parse(localStorage.getItem('user')).orgId;
        return queryData(Object.assign(parameter, _this2.queryParam, {
          orgId: orgId
        }), '/cmdCollection/getCmdCollectionPage').then(function (res) {
          console.log(res);
          return res.result;
        });
      },
      mapList: [],
      roleNames: [],
      selectedRowKeys: []
    };
  },
  created: function created() {
    this.getMap();
  },
  computed: {
    title: function title() {
      return this.$route.meta.title;
    }
  },
  methods: {
    // 获取该单位下的地图
    getMap: function getMap() {
      var _this3 = this;

      var org = JSON.parse(localStorage.getItem('user')).orgId;
      queryData({
        orgId: org
      }, '/robotMapLib/getRobotMapLibList').then(function (res) {
        console.log('-----------getMap', res);
        _this3.mapList = res.result;
      });
    },
    onChange: function onChange(date, dateString) {
      this.queryParam.validityDate = dateString;
      console.log(date, dateString);
    },
    onSelectChange: function onSelectChange(selectedRowKeys) {
      this.selectedRowKeys = selectedRowKeys;
    },
    // 处理新增
    handleAddSubmit: function handleAddSubmit() {
      this.$refs.table.refresh(true);
    },
    // 表单重置
    resetQuery: function resetQuery() {
      this.queryParam = {};
      this.$refs.table.refresh(true);
    },
    // 显示状态修改
    handleAddVisible: function handleAddVisible() {
      this.addVisible = true;
    },
    // 编辑动作集
    handleEditVisible: function handleEditVisible(record) {
      // let mapId = this.editSkills.map == undefined ? 0 : this.editSkills.map
      // let cmd = this.editSkills.cmd == undefined ? 0 : this.editSkills.cmd
      // console.log("编辑动作集"+mapId+"/"+cmd +"/"+ this.businessName +"/"+ this.skillName)
      // this.$refs.cmd.show(this.businessName + this.skillName,mapId,cmd)
      console.info(record); // this.editData = record
      // this.$refs.editModal.add(record)

      this.$refs.EditCmdColl.add(record);
    },
    filterOption: function filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0;
    },
    // 搜索内容
    searchData: function searchData() {
      this.$refs.table.refresh(true);
    },
    handleOk: function handleOk() {
      this.$refs.table.refresh(true);
    },
    // 删除命令集
    handleDelete: function handleDelete(record) {
      var _this = this;

      this.$confirm({
        title: '警告',
        content: "\u771F\u7684\u8981\u5220\u9664 ".concat(record.name, " \u5417?"),
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk: function onOk() {
          console.log('OK'); // 在这里调用删除接口

          return new Promise(function (resolve, reject) {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500);
            var id = {
              entityId: record.id
            };
            queryData(id, '/cmdCollection/delete').then(function (res) {
              console.log('删除返回值：', res);

              if (res.code == 0) {
                _this.$message.success('删除成功');

                _this.$refs.table.refresh(true);
              } else {
                _this.$message.error(res.message);
              }
            });
          }).catch(function () {
            return console.log('Oops errors!');
          });
        },
        onCancel: function onCancel() {
          console.log('Cancel');
        }
      });
    }
  }
};
