      var _this = this;

      this.$confirm({
        title: '警告',
        content: "\u7B49\u5F85\u65F6\u95F4\u53EF\u80FD\u8FC7\u957F\uFF0C\u662F\u5426\u786E\u8BA4?",
        okText: '确认',
        okType: 'danger',
        cancelText: '退出',
