    deleteSkillsIds: function deleteSkillsIds() {
      console.log(this.selectedRowKeys);

      var _this = this;

      this.$confirm({
        title: '警告',
        content: "\u771F\u7684\u8981\u5220\u9664\u5417?",
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk: function onOk() {
          // 在这里调用删除接口
          return new Promise(function (resolve, reject) {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500);
            var ids = {
              entityIds: [_this.selectedRowKeys][0]
