            queryData(ids, '/skills/delete').then(function (res) {
              if (res.code == 0) {
                _this.$message.success('删除成功');

                _this.selectedRowKeys = [];

                _this.$refs.table.refresh(true);
              } else {
                _this.$message.error(res.message);
              }
            });
          }).catch(function () {
            return console.log('Oops errors!');
          });
        },
        onCancel: function onCancel() {
          console.log('Cancel');
        }
      });
    },
    //清空动作集
    delCmd: function delCmd(record) {
      var _this = this;

      this.$confirm({
        title: '警告',
        content: " <".concat(record.title, "> \u5C06\u4E0D\u518D\u4F7F\u7528 <").concat(record.cmdName, "> \u52A8\u4F5C\u96C6"),
