import { addData, queryData } from '@/api/common';
import { STable } from '@/components';
export default {
  name: 'TableList',
  components: {
    STable: STable
  },
  data: function data() {
    return {
      returnCmd: '',
      // confirmLoading: false,
      form: this.$form.createForm(this),
      showIframe: false,
      cmdSrc: 'http://robot.lovechuangchuang.com:8084/robot.html' // cmdSrc: 'http://192.168.0.154:8084/robot.html'

    };
  },
  // 添加监听，监听子页面发送的值
  mounted: function mounted() {
    var _this = this;

    window.addEventListener('message', function (e) {
      _this.returnCmd = e.data;
    });
  },
  methods: {
    // 打开网页传值
    show: function show(title, mapId, cmd) {
      var user = JSON.parse(localStorage.getItem('user'));
      var param = '?orgId=' + user.orgId + '&createBy=' + user.id + '&circulation=1' + '&mapId=' + mapId + '&title=' + title + '&cmd=' + cmd;
      this.cmdSrc = this.cmdSrc + encodeURI(param);
      console.log(this.cmdSrc);
      this.showIframe = true;
    },
    // 右上角关闭按钮事件
    handleSubmit: function handleSubmit() {
      if (this.returnCmd != '') {
        // 命令集id回传到编辑页面
        this.$emit('getCmdId', this.returnCmd);
      }

      this.showIframe = false;
    }
  }
};
