import { STable } from '@/components';
import { PageView } from '@/layouts';
import CreateAiuiScene from "./modules/CreateAiuiScene";
import EditAiuiScene from "./modules/EditAiuiScene";
import { queryData } from '@/api/common';
export default {
  name: 'TableList',
  components: {
    STable: STable,
    PageView: PageView,
    CreateAiuiScene: CreateAiuiScene,
    EditAiuiScene: EditAiuiScene
  },
  data: function data() {
    var _this2 = this;

    return {
      // 高级搜索 展开/关闭
      advanced: false,
      // 查询参数
      queryParam: {
        aiuiName: '',
        aiuiDescn: ''
      },
      columns: [{
        title: '#',
        dataIndex: 'no',
        scopedSlots: {
          customRender: 'serial'
        }
      }, {
        title: '名称',
        dataIndex: 'scene',
        scopedSlots: {
          customRender: 'scene'
        }
      }, {
        title: '描述',
        dataIndex: 'descn',
        scopedSlots: {
          customRender: 'descn'
        }
      }, {
        title: '操作',
        dataIndex: 'action',
        scopedSlots: {
          customRender: 'action'
        }
      }],
      // 加载数据方法 必须为 Promise 对象
      loadData: function loadData(parameter) {
        return queryData(Object.assign(parameter, _this2.queryParam), '/aiuiScene/list').then(function (res) {
          console.log(res);
          return res.result;
        });
      }
    };
  },
  methods: {
    filterOption: function filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0;
    },
    // 表单重置
    resetQuery: function resetQuery() {
      this.queryParam = {};
      this.$refs.table.refresh(true);
    },
    handleOk: function handleOk() {
      console.log('呵呵');
      this.$refs.table.refresh(true);
    },
    handleUpdate: function handleUpdate(record) {
      this.$refs.editAiui.add(record);
    },
    handleAdd: function handleAdd() {
      this.$refs.createAiui.add();
    },
    //禁用
    handleIsAble: function handleIsAble(checked, record) {
      console.log(checked);
      var param = {
        entityId: record.id
      };

      if (checked) {
        param.isAble = 1;
      } else {
        param.isAble = 0;
      }

      queryData(param, '/aiuiScene/isAble').then(function (res) {
        _this.orgNames = res.result;

        if (res.code !== 0) {
          _this.$message.error(res.message);
        }
      }).catch(function (err) {
        return console.log(err);
      });
    },
    //删除
    deleteSkillsType: function deleteSkillsType(record) {
      var _this = this;

      this.$confirm({
        title: '警告',
        content: "\u771F\u7684\u8981\u5220\u9664 ".concat(record.scene, " \u5417?"),
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk: function onOk() {
          console.log('OK'); // 在这里调用删除接口

          return new Promise(function (resolve, reject) {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 1000);
            var id = {
              entityId: record.id
            };
            queryData(id, '/aiuiScene/delete').then(function (res) {
              if (res.code === 0) {
                _this.$message.success('删除成功');

                _this.$refs.table.refresh(true);
              } else {
                _this.$message.error(res.message);
              }
            });
          }).catch(function () {
            return console.log('Oops errors!');
          });
        },
        onCancel: function onCancel() {
          console.log('Cancel');
        }
      });
    }
  }
};
