import { DetailList } from '@/components';
import SettingItem from "./SettingItem";
import config from '@/config/defaultSettings';
import { updateTheme, updateColorWeak, colorList } from "./settingConfig";
import { mixin, mixinDevice } from '@/utils/mixin';
export default {
  components: {
    DetailList: DetailList,
    SettingItem: SettingItem
  },
  mixins: [mixin, mixinDevice],
  data: function data() {
    return {
      visible: true,
      colorList: colorList
    };
  },
  watch: {},
  mounted: function mounted() {
    var vm = this;
    setTimeout(function () {
      vm.visible = false;
    }, 16);
    updateTheme(this.primaryColor);

    if (this.colorWeak !== config.colorWeak) {
      updateColorWeak(this.colorWeak);
    }
  },
  methods: {
    showDrawer: function showDrawer() {
      this.visible = true;
    },
    onClose: function onClose() {
      this.visible = false;
    },
    toggle: function toggle() {
      this.visible = !this.visible;
    },
    onColorWeak: function onColorWeak(checked) {
      this.$store.dispatch('ToggleWeak', checked);
      updateColorWeak(checked);
    },
    onMultiTab: function onMultiTab(checked) {
      this.$store.dispatch('ToggleMultiTab', checked);
    },
    handleMenuTheme: function handleMenuTheme(theme) {
      this.$store.dispatch('ToggleTheme', theme);
    },
    doCopy: function doCopy() {
      var _this = this;

      // get current settings from mixin or this.$store.state.app, pay attention to the property name
      var text = "export default {\n  primaryColor: '".concat(this.primaryColor, "', // primary color of ant design\n  navTheme: '").concat(this.navTheme, "', // theme for nav menu\n  layout: '").concat(this.layoutMode, "', // nav menu position: sidemenu or topmenu\n  contentWidth: '").concat(this.contentWidth, "', // layout of content: Fluid or Fixed, only works when layout is topmenu\n  fixedHeader: ").concat(this.fixedHeader, ", // sticky header\n  fixSiderbar: ").concat(this.fixSiderbar, ", // sticky siderbar\n  autoHideHeader: ").concat(this.autoHideHeader, ", //  auto hide header\n  colorWeak: ").concat(this.colorWeak, ",\n  multiTab: ").concat(this.multiTab, ",\n  production: process.env.NODE_ENV === 'production' && process.env.VUE_APP_PREVIEW !== 'true',\n  // vue-ls options\n  storageOptions: {\n    namespace: 'pro__',\n    name: 'ls',\n    storage: 'local',\n  }\n}");
      this.$copyText(text).then(function (message) {
        console.log('copy', message);

        _this.$message.success('复制完毕');
      }).catch(function (err) {
        console.log('copy.err', err);

        _this.$message.error('复制失败');
      });
    },
    handleLayout: function handleLayout(mode) {
      this.$store.dispatch('ToggleLayoutMode', mode); // 因为顶部菜单不能固定左侧菜单栏，所以强制关闭

      this.handleFixSiderbar(false);
    },
    handleContentWidthChange: function handleContentWidthChange(type) {
      this.$store.dispatch('ToggleContentWidth', type);
    },
    changeColor: function changeColor(color) {
      if (this.primaryColor !== color) {
        this.$store.dispatch('ToggleColor', color);
        updateTheme(color);
      }
    },
    handleFixedHeader: function handleFixedHeader(fixed) {
      this.$store.dispatch('ToggleFixedHeader', fixed);
    },
    handleFixedHeaderHidden: function handleFixedHeaderHidden(autoHidden) {
      this.$store.dispatch('ToggleFixedHeaderHidden', autoHidden);
    },
    handleFixSiderbar: function handleFixSiderbar(fixed) {
      if (this.layoutMode === 'topmenu') {
        this.$store.dispatch('ToggleFixSiderbar', false);
        return;
      }

      this.$store.dispatch('ToggleFixSiderbar', fixed);
    }
  }
};
