import { queryData } from '@/api/common';
import CmdList from "../../Cmd";
export default {
  components: {
    CmdList: CmdList
  },
  data: function data() {
    return {
      headers: {
        authorization: 'authorization-text'
      },
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 7
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 13
        }
      },
      cmdList: [],
      visible: false,
      form: this.$form.createForm(this),
      editData: {
        title: ''
      },
      mapList: [],
      cmd: '',
      circulation: ''
    };
  },
  created: function created() {
    this.getMap();
  },
  methods: {
    changeMap: function changeMap(event) {
      // console.log('选择项: ' + event)
      this.editData.mapId = event;
    },
    add: function add() {
      this.cmd = '';
      this.form.resetFields();
      this.visible = true;
    },
    getMap: function getMap() {
      var _this = this;

      var org = JSON.parse(localStorage.getItem('user')).orgId;
      queryData({
        orgId: org
      }, '/robotMapLib/getRobotMapLibList').then(function (res) {
        // console.log('-----------getMap', res);
        _this.mapList = res.result;
      });
    },
    getCmdId: function getCmdId(cmdId) {
      console.log("收到参数cmd=" + cmdId);
      this.$emit('ok');
      this.visible = false;
    },
    showCmd: function showCmd() {
      var mapId = this.editData.mapId == undefined ? 0 : this.editData.mapId; // console.log('地图ID:' + mapId)

      this.$refs.cmd.show('', mapId, 0);
    }
  }
};
