import "core-js/modules/es7.array.includes";
import _objectSpread from "E:\\XiangMu\\ant-design-pro-vue\\node_modules\\@babel\\runtime-corejs2/helpers/esm/objectSpread";
import Vue from 'vue';
import router from "./router";
import store from "./store";
import NProgress from 'nprogress'; // progress bar

import 'nprogress/nprogress.css'; // progress bar style

import notification from 'ant-design-vue/es/notification';
import { setDocumentTitle, domTitle } from '@/utils/domUtil';
import { ACCESS_TOKEN, USER_ROLE } from '@/store/mutation-types';
import { cutStrByFullLength } from "./components/_util/util"; // 绿色进度条

NProgress.configure({
  showSpinner: false
}); // NProgress Configuration

var whiteList = ['login', 'register', 'registerResult']; // no redirect whitelist

router.beforeEach(function (to, from, next) {
  NProgress.start(); // start progress bar、

  to.meta && typeof to.meta.title !== 'undefined' && setDocumentTitle("".concat(to.meta.title, " - ").concat(domTitle));

  if (Vue.ls.get(ACCESS_TOKEN)) {
    if (to.path === '/user/login') {
      next({
        path: '/dashboard/workplace'
      });
      NProgress.done();
    } else {
      if (store.getters.roles.length === 0) {
        store.dispatch('GetInfo', Vue.ls.get(USER_ROLE)).then(function (res) {
          var roles = res.result && res.result.role;
          store.dispatch('GenerateRoutes', {
            roles: roles
          }).then(function () {
            // 根据roles权限生成可访问的路由表
            // 动态添加可访问路由表
            // store.getters.addRouters 是 router.config.js
            router.addRoutes(store.getters.addRouters); // decodeURIComponent解码

            var redirect = decodeURIComponent(from.query.redirect || to.path);

            if (to.path === redirect) {
              // hack方法 确保addRoutes已完成 ,set the replace: true so the navigation will not leave a history record
              next(_objectSpread({}, to, {
                replace: true
              }));
            } else {
              // 跳转到目的路由
              next({
                path: redirect
              });
            }
          });
        }).catch(function () {
          notification.error({
            message: '错误',
            description: '请求用户信息失败，请重试'
          });
          store.dispatch('Logout').then(function () {
            next({
              path: '/user/login',
              query: {
                redirect: to.fullPath
              }
            });
          });
        });
      } else {
        next();
      }
    }
  } else {
    console.log('access_token 为空');

    if (whiteList.includes(to.name)) {
      // 在免登录白名单，直接进入
      next();
    } else {
      next({
        path: '/user/login',
        query: {
          redirect: to.fullPath
        }
      });
      NProgress.done(); // if current page is login will not trigger afterEach hook, so manually handle it
    }
  }
});
router.afterEach(function () {
  NProgress.done(); // finish progress bar
});
