import { queryData } from '@/api/common'; // 新增闲聊已经不需要这个编辑动作集的组件了

import CmdList from "../../Cmd";
export default {
  components: {
    CmdList: CmdList
  },
  data: function data() {
    return {
      showEditCmd: false,
      headers: {
        authorization: 'authorization-text'
      },
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 7
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 13
        }
      },
      cmdList: [],
      visible: false,
      isAble: true,
      skillList: [],
      selectSkills: [],
      form: this.$form.createForm(this),
      mapList: [],
      cmd: ''
    };
  },
  methods: {
    changeCmd: function changeCmd(event) {
      this.cmd = event;
      this.showEditCmd = true;
    },
    // 修改动作集
    editCmd: function editCmd() {
      var formData = this.form.getFieldsValue();
      var mapId = formData.mapId == undefined ? 0 : formData.mapId;
      this.$refs.cmd.show('编辑闲聊令集', mapId, this.cmd);
    },
    // 新增动作集
    addCmd: function addCmd() {
      var formData = this.form.getFieldsValue();
      var mapId = formData.mapId == undefined ? 0 : formData.mapId;
      this.$refs.cmd.show('编辑闲聊动作集', mapId, 0);
    },
    add: function add() {
      this.showEditCmd = false;
      this.visible = true;
      this.isAble = true;
      this.getCmd();
      this.getMap();
      this.cmd = '';
    },
    // 获取地图列表
    getMap: function getMap() {
      var _this2 = this;

      var org = JSON.parse(localStorage.getItem('user')).orgId;
      queryData({
        orgId: org
      }, '/robotMapLib/getRobotMapLibList').then(function (res) {
        _this2.mapList = res.result;
      });
    },
    // 新增时配置动作集（参数都为初始值）
    updateCmd: function updateCmd() {
      var formData = this.form.getFieldsValue();
      var mapId = formData.mapId == undefined ? 0 : formData.mapId;
      var title = formData.title == undefined ? '闲聊动作集' : formData.title;
      this.$refs.cmd.show(title, 0, mapId);
    },
    getCmdId: function getCmdId(cmdId) {
      this.getCmd();

      if (cmdId != 0) {
        this.cmd = parseInt(cmdId);
        this.form.setFieldsValue({
          cmd: parseInt(cmdId)
        });
      }
    },
    // 获取动作集列表
    getCmd: function getCmd() {
      var org = JSON.parse(localStorage.getItem('user')).orgId;

      var _this = this;

      queryData({
        orgId: org
      }, '/cmdCollection/getCmdListByOrg').then(function (res) {
        if (res.code == 0) {
          _this.cmdList = res.cmdList;
        } else {
          _this.$message.error("获取动作集列表失败");
        }
      });
    },
    handleSubmit: function handleSubmit() {
      var _this3 = this;

      var validateFields = this.form.validateFields;
      this.confirmLoading = true;
      validateFields(function (errors, values) {
        if (!errors) {
          var _this = _this3;
          var user = JSON.parse(localStorage.getItem('user'));
          var orgId = user.orgId;
          var userId = user.id;
          var param = {
            orgId: orgId,
            user: userId,
            type: 2
          };
          values.isAble = values.isAble == false ? 0 : 1;
          values.url = values.url;
          queryData(Object.assign(param, values), '/skills/saveOrUpdateXL').then(function (res) {
            console.log(res);

            if (res.code == 0) {
              _this.$message.success('添加成功');

              _this.visible = false;

              _this.$emit('ok', values);

              _this.cmd = '';
            } else {
              _this.$message.error(res.message);
            }
          });
        }
      });
    }
  }
};
