import { STable } from '@/components';
import EditModal from "./modules/EditFace";
import CreateModal from "./modules/CreateFace";
import RobotCmd from "./modules/RobotCmd";
import EditRobot from "./modules/EditRobotcmd";
import { queryData } from '@/api/common';
export default {
  name: 'TableList',
  components: {
    STable: STable,
    EditModal: EditModal,
    CreateModal: CreateModal,
    RobotCmd: RobotCmd,
    EditRobot: EditRobot
  },
  data: function data() {
    var _this2 = this;

    return {
      roleId: 0,
      roleName: '',
      mdl: {},
      // 查询参数
      queryParam: {
        name: ''
      },
      // 表头
      columns: [{
        title: 'id',
        dataIndex: 'id'
      }, {
        title: '人脸名称',
        dataIndex: 'name'
      }, {
        title: '图片',
        dataIndex: 'photo'
      }, {
        title: '创建时间',
        dataIndex: 'actionDate'
      }, {
        title: '操作',
        dataIndex: 'action',
        scopedSlots: {
          customRender: 'action'
        }
      }],
      loadData: function loadData(parameter) {
        var orgId = JSON.parse(localStorage.getItem('user')).orgId;
        return queryData(Object.assign(parameter, _this2.queryParam, {
          org: orgId
        }), '/faceManage/queryFaceMsg').then(function (res) {
          return res.result;
        });
      }
    };
  },
  methods: {
    handleEdit: function handleEdit(record) {
      this.$refs.editModal.edit(record);
    },
    UpdateRobotCmd: function UpdateRobotCmd() {
      this.$message.error('修改');
    },
    // 删除某一个机器人命令
    DelRobotCmd: function DelRobotCmd(record) {
      console.log(record);

      var _this = this;

      this.$confirm({
        title: '警告',
        content: "\u771F\u7684\u8981\u5220\u9664 ".concat(record.robotCode, " \u7684\u547D\u4EE4\u96C6\u5417?"),
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk: function onOk() {
          console.log('OK'); // 在这里调用删除接口

          return new Promise(function (resolve, reject) {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 300);
            var id = {
              id: [record.id]
            };
            queryData(id, '/faceManage/delRobotFace').then(function (res) {
              if (res.code == 0) {
                _this.$message.success('删除成功');

                _this.$refs.table.refresh(true);
              } else {
                _this.$message.error(res.message);
              }
            });
          }).catch(function () {
            return console.log('Oops errors!');
          });
        },
        onCancel: function onCancel() {
          console.log('Cancel');
        }
      });
    },
    // 配置机器人命令集
    handleRobotCmd: function handleRobotCmd(record) {
      this.$refs.robotCmd.show(record);
    },
    handleOk: function handleOk() {
      // 新增/修改 成功时，重载列表
      this.$refs.table.refresh(true);
    },
    // 处理重置
    handleReset: function handleReset() {
      this.queryParam.name = '';
      this.$refs.table.refresh();
    },
    // 删除
    handleDel: function handleDel(record) {
      var _this = this;

      this.$confirm({
        title: '警告',
        content: "\u771F\u7684\u8981\u5220\u9664 ".concat(record.name, " \u5417?"),
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk: function onOk() {
          console.log('OK'); // 在这里调用删除接口

          return new Promise(function (resolve, reject) {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 300);
            var id = {
              id: [{
                id: record.id,
                photo: record.photo
              }]
            };
            queryData(id, '/faceManage/delFaceMsg').then(function (res) {
              if (res.code == 0) {
                _this.$message.success('删除成功');

                _this.$refs.table.refresh(true);
              } else {
                _this.$message.error(res.message);
              }
            });
          }).catch(function () {
            return console.log('Oops errors!');
          });
        },
        onCancel: function onCancel() {
          console.log('Cancel');
        }
      });
    }
  }
};
