import { addData, queryData } from '@/api/common';
import { STable } from '@/components';
import CmdList from "../../Cmd";
export default {
  name: 'TableList',
  components: {
    STable: STable,
    CmdList: CmdList
  },
  data: function data() {
    return {
      showEditCmd: false,
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 7
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 13
        }
      },
      visible: false,
      confirmLoading: false,
      cmdList: [],
      editData: [],
      cmd: '',
      form: this.$form.createForm(this),
      showIframe: false,
      mapList: []
    };
  },
  created: function created() {// this.getCmd()
    // this.getMap()
  },
  methods: {
    changeCmd: function changeCmd(event) {
      this.editData.cmd = event;
      this.showEditCmd = true;
    },
    show: function show(record) {
      console.log(record);

      if (record.cmd != '') {
        this.showEditCmd = true;
      } else {
        this.showEditCmd = false;
      }

      console.log(this.showEditCmd);
      this.editData = record;
      this.visible = true;
      this.getCmd();
      this.getMap();
    },
    // 修改命令集
    editCmd: function editCmd() {
      console.log('编辑机器人命令集' + this.editData.cmd); // if (this.editData.cmd == 0 || this.editData.cmd == '') {
      //   this.$message.error('请先选择要修改的命令集')
      //   return
      // }

      this.$refs.cmd.show('编辑机器人命令集', '', this.editData.cmd);
    },
    // 新增命令集
    addCmd: function addCmd() {
      this.$refs.cmd.show('编辑机器人命令集', '', 0);
    },
    // 编辑命令集后，获取命令集ID
    getCmdId: function getCmdId(cmd) {
      this.getCmd();
      this.editData.cmd = cmd;
      this.form.setFieldsValue({
        cmd: parseInt(cmd)
      });
    },
    getMap: function getMap() {
      var _this2 = this;

      var org = JSON.parse(localStorage.getItem('user')).orgId;
      queryData({
        orgId: org
      }, '/robotMapLib/getRobotMapLibList').then(function (res) {
        console.log('-----------getMap', res);
        _this2.mapList = res.result;
      });
    },
    getCmd: function getCmd() {
      var org = JSON.parse(localStorage.getItem('user')).orgId;

      var _this = this;

      queryData({
        orgId: org
      }, '/cmdCollection/getCmdListByOrg').then(function (res) {
        console.log(res);

        if (res.code == 0) {
          _this.cmdList = res.cmdList;
        } else {
          _this.$message.error(res.message);
        }
      });
    },
    handleSubmit: function handleSubmit() {
      var _this3 = this;

      var validateFields = this.form.validateFields;
      this.confirmLoading = true;
      validateFields(function (errors, values) {
        if (!errors) {
          var _this = _this3;
          setTimeout(function () {
            var param = {
              id: _this3.editData.id,
              cmd: values.cmd
            };
            addData(param, '/faceManage/editRobotFace').then(function (res) {
              if (res.code == 0) {
                _this.$message.success('修改成功'); // 重置输入框内容


                _this.form.resetFields();

                _this3.visible = false;
                _this3.confirmLoading = false;

                _this3.$emit('ok', values);
              } else {
                _this.$message.error(res.message);
              }
            });
          }, 300);
        } else {
          _this3.confirmLoading = false;
        }
      });
    },
    filterOption: function filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0;
    }
  }
};
