import { STable } from '@/components';
import { PageView } from '@/layouts';
import { queryData } from '@/api/common';
import CreateModel from "./modules/CreateTouchcmd";
import EditModel from "./modules/EditTouchcmd";
export default {
  name: 'TableList',
  components: {
    STable: STable,
    PageView: PageView,
    CreateModel: CreateModel,
    EditModel: EditModel
  },
  data: function data() {
    var _this2 = this;

    return {
      // 高级搜索 展开/关闭
      advanced: false,
      // 查询参数
      queryParam: {
        wakekey: '',
        unitName: ''
      },
      cmdTypeList: [],
      // 表头
      columns: [{
        title: '#',
        dataIndex: 'no',
        scopedSlots: {
          customRender: 'serial'
        }
      }, {
        title: '触发条件',
        dataIndex: 'triggerName',
        scopedSlots: {
          customRender: 'triggerName'
        }
      }, {
        title: '指令编码',
        dataIndex: 'code',
        scopedSlots: {
          customRender: 'code'
        }
      }, {
        title: '操作',
        dataIndex: 'action',
        scopedSlots: {
          customRender: 'action'
        }
      }],
      // 加载数据方法 必须为 Promise 对象
      loadData: function loadData(parameter) {
        return queryData(Object.assign(parameter, _this2.queryParam), '/supercmdTrigger/list').then(function (res) {
          console.log(res);
          return res.result;
        });
      }
    };
  },
  computed: {
    title: function title() {
      return this.$route.meta.title;
    }
  },
  methods: {
    // 处理刷新
    handleRefresh: function handleRefresh() {
      this.$refs.table.refresh(true);
    },
    // 表单重置
    resetQuery: function resetQuery() {
      this.queryParam = {};
      this.$refs.table.refresh(true);
    },
    handleEditVisible: function handleEditVisible(record) {
      this.$refs.editModal.add(record);
    },
    // 搜索内容
    searchData: function searchData() {
      this.$refs.table.refresh(true);
    },
    // 删除
    handleDelete: function handleDelete(record) {
      var _this = this;

      this.$confirm({
        title: '警告',
        content: "\u771F\u7684\u8981\u5220\u9664 ".concat(record.triggerName, " \u5417?"),
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk: function onOk() {
          console.log('OK'); // 在这里调用删除接口

          return new Promise(function (resolve, reject) {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 200);
            var id = {
              entityId: record.id
            };
            queryData(id, '/supercmdTrigger/delete').then(function (res) {
              if (res.code == 0) {
                _this.$message.success('删除成功');

                _this.$refs.table.refresh(true);
              } else {
                _this.$message.error(res.message);
              }
            });
          }).catch(function () {
            return console.log('Oops errors!');
          });
        },
        onCancel: function onCancel() {
          console.log('Cancel');
        }
      });
    }
  }
};
