import { STable } from '@/components';
import { PageView } from '@/layouts';
import EditForm from "./modules/EditFile";
import CreateFile from "./modules/CreateFile";
import { queryData } from '@/api/common';
export default {
  name: 'TableList',
  components: {
    STable: STable,
    PageView: PageView,
    EditForm: EditForm,
    CreateFile: CreateFile
  },
  data: function data() {
    var _this2 = this;

    return {
      headers: {
        authorization: 'authorization-text'
      },
      // 查询参数
      queryParam: {
        appVersion: '',
        orgId: ''
      },
      orgNames: [],
      editData: {},
      options: {
        alert: false,
        rowSelection: null
      },
      // 表头
      columns: [{
        title: '#',
        dataIndex: 'no',
        scopedSlots: {
          customRender: 'serial'
        }
      }, {
        title: '文件名称',
        dataIndex: 'fileName'
      }, {
        title: '文件类型',
        dataIndex: 'fileType'
      }, {
        title: '文件访问路径',
        dataIndex: 'fileUrl'
      }, {
        title: '所属单位',
        dataIndex: 'orgName'
      }, {
        title: '操作',
        dataIndex: 'action',
        scopedSlots: {
          customRender: 'action'
        }
      }],
      // 加载数据方法 必须为 Promise 对象
      loadData: function loadData(parameter) {
        console.log('App 请求', parameter);
        var orgId;

        if (_this2.queryParam.orgId > 0) {
          orgId = _this2.queryParam.orgId;
        } else {
          orgId = JSON.parse(localStorage.getItem('user')).orgId;
        }

        return queryData(Object.assign(parameter, _this2.queryParam, {
          orgId: orgId
        }), '/skillOptionFile/queryFileList').then(function (res) {
          console.log(res);
          return res.result;
        });
      }
    };
  },
  computed: {
    title: function title() {
      return this.$route.meta.title;
    }
  },
  created: function created() {
    this.handleOrgLists();
  },
  methods: {
    // 获取该用户下的所属单位
    handleOrgLists: function handleOrgLists() {
      var _this = this;

      var orgId = JSON.parse(localStorage.getItem('user')).orgId;
      var param = {
        org: orgId
      };
      queryData(param, '/userManage/getHrOrgByPid').then(function (res) {
        _this.orgNames = res.result;

        if (res.code !== 0) {
          _this.$message.error(res.message);
        }
      }).catch(function (err) {
        return console.log(err);
      });
    },
    // 下拉框
    filterOption: function filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0;
    },
    handleChange: function handleChange(info) {
      if (info.file.status !== 'uploading') {
        console.log(info.file, info.fileList);
      }

      if (info.file.status === 'done') {
        this.$message.success("".concat(info.file.name, " file uploaded successfully"));
      } else if (info.file.status === 'error') {
        this.$message.error("".concat(info.file.name, " file upload failed."));
      }
    },
    // 处理更新
    handleRefresh: function handleRefresh() {
      this.$refs.table.refresh();
    },
    // 表单重置
    resetQuery: function resetQuery() {
      this.queryParam = {};
      this.$refs.table.refresh(true);
    },
    handleEditVisible: function handleEditVisible(record) {
      this.$refs.editModal.add(record);
    },
    // 搜索内容
    searchData: function searchData() {
      this.$refs.table.refresh(true);
    },
    // 删除
    handleDeleteUser: function handleDeleteUser(record) {
      var _this = this;

      this.$confirm({
        title: '警告',
        content: "\u771F\u7684\u8981\u5220\u9664 ".concat(record.fileName, " \u5417?"),
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk: function onOk() {
          console.log('OK'); // 在这里调用删除接口

          return new Promise(function (resolve, reject) {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500);
            var param = {
              id: record.id,
              fileUrl: record.fileUrl
            };
            queryData(param, '/skillOptionFile/delFile').then(function (res) {
              console.log('删除返回值：', res);

              if (res.code == 0) {
                _this.$message.success('删除成功');

                _this.$refs.table.refresh();
              } else {
                _this.$message.error(res.message);
              }
            });
          }).catch(function () {
            return console.log('Oops errors!');
          });
        },
        onCancel: function onCancel() {
          console.log('Cancel');
        }
      });
    }
  }
};
