export default {
  name: 'RouteView',
  props: {
    keepAlive: {
      type: Boolean,
      default: true
    }
  },
  data: function data() {
    return {};
  },
  render: function render() {
    var h = arguments[0];
    var meta = this.$route.meta,
        getters = this.$store.getters;
    var inKeep = h("keep-alive", [h("router-view")]);
    var notKeep = h("router-view"); // 这里增加了 multiTab 的判断，当开启了 multiTab 时
    // 应当全部组件皆缓存，否则会导致切换页面后页面还原成原始状态
    // 若确实不需要，可改为 return meta.keepAlive ? inKeep : notKeep
    // if (!getters.multiTab && meta.keepAlive === false) {
    //   return notKeep
    // }
    // return this.keepAlive || getters.multiTab || meta.keepAlive ? inKeep : notKeep

    if (meta.keepAlive === true) {
      return inKeep;
    } else {
      return notKeep;
    }
  }
};
