import { queryData } from '@/api/common';
export default {
  props: ['skillsId', 'skillTableRefresh'],
  data: function data() {
    return {
      headers: {
        authorization: 'authorization-text'
      },
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 7
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 13
        }
      },
      visible: false,
      skillList: [],
      selectSkills: []
    };
  },
  methods: {
    add: function add() {
      this.visible = true;
      this.getSkillList();
    },
    handleChange: function handleChange(info) {
      if (info.file.status !== 'uploading') {
        console.log(info.file, info.fileList);
      }

      if (info.file.status === 'done') {
        this.$message.success("\u4E0A\u4F20\u6210\u529F");
      } else if (info.file.status === 'error') {
        this.$message.error("\u4E0A\u4F20\u5931\u8D25");
      }
    },
    afterClose: function afterClose() {},
    handleSubmit: function handleSubmit() {
      var _this = this;

      var orgId = JSON.parse(localStorage.getItem('user')).orgId;
      var skills = this.selectSkills.map(function (value, index) {
        return _this.skillList[value];
      });
      var param = {
        user: orgId,
        skillsId: this.skillsId,
        robotSkillList: skills
      };
      queryData(param, '/skillsOption/saveList').then(function (res) {
        console.log(res.result);

        if (res.code == 0) {
          _this.$message.success('添加成功');

          _this.visible = false;

          _this.skillTableRefresh();
        } else {
          _this.$message.error(res.message);
        }
      });
    }
  }
};
