import { STable } from '@/components'

import UpdateExcel from './modules/UpdateExcel'
import EditChat from './modules/EditChat'
import CreateChat from './modules/CreateChat'
import { queryData } from '@/api/common'
export default {
  name: 'TableList',
  components: {
    STable,
    UpdateExcel,
    EditChat,
    CreateChat
  },
  data() {
    return {
      // 高级搜索 展开/关闭
      advanced: false,
      selectedItems: [],
      // 查询参数
      queryParam: {
        keywords: ''
      },
      editData: {},
      // 表头
      columns: [
        {
          title: '#',
          dataIndex: 'no',
          scopedSlots: { customRender: 'serial' }
        },

        {
          title: '标题',
          dataIndex: 'title',
          scopedSlots: { customRender: 'name' }
        },
        {
          title: '回答内容',
          dataIndex: 'contents',
          scopedSlots: { customRender: 'contents' }
        },
        {
          title: '网页或图片',
          dataIndex: 'url',
          scopedSlots: { customRender: 'url' }
        },
        {
          title: '命令集',
          dataIndex: 'cmdName',
          scopedSlots: { customRender: 'cmdName' }
        },
        // {
        //   title: '是否生效',
        //   dataIndex: 'isDel',

        //   scopedSlots: { customRender: 'isDel' }
        // },
        {
          title: '操作',
          dataIndex: 'action',
          scopedSlots: { customRender: 'action' }
        }
      ],
      // 加载数据方法 必须为 Promise 对象
      loadData: parameter => {
        let orgId = JSON.parse(localStorage.getItem('user')).orgId
        return queryData(
          Object.assign(parameter, { orgId: orgId, type: 2 }, this.queryParam),
          '/skills/getSkillsList'
        ).then(res => {
          console.log(res)
          return res.result
        })
      },
      orgNames: [],
      roleNames: [],
      selectedRowKeys: [],
      selectedRows: [],
      options: {
        alert: false,
        rowSelection: null
      },
      optionAlertShow: false
    }
  },
  created() {
  },
  methods: {
    deriveExcel() {},

    tableOption() {
      if (!this.optionAlertShow) {
        this.options = {
          alert: {
            show: true,
            clear: () => {
              this.selectedRowKeys = []
            }
          },
          rowSelection: {
            selectedRowKeys: this.selectedRowKeys,
            onChange: this.onSelectChange,
            getCheckboxProps: record => ({
              props: {
                disabled: record.no === 'No 2', 
                name: record.no
              }
            })
          }
        }
        this.optionAlertShow = true
      } else {
        this.options = {
          alert: false,
          rowSelection: null
        }
        this.optionAlertShow = false
      }
    },

    onSelectChange(selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },

    // 处理新增
    handleAddSubmit() {
      this.$refs.table.refresh(true)
    },
    // 表单重置
    resetQuery() {
      ;(this.queryParam = {
        title: '',
        keywords: ''
      }),
        this.$refs.table.refresh(true)
    },
    // 显示状态修改
    handleAddVisible() {
      this.addVisible = true
    },
    //修改闲聊
    handleEditVisible(record) {
      this.editData = record
      // this.$refs.editModal.add(record)
       this.$refs.EditChat.add(record)
    },
    // 搜索内容
    searchData() {
      this.$refs.table.refresh(true)
    },
    mieBa(){
      var _this = this
      this.$confirm({
        title: '警告',
        content: `等待时间可能过长，是否确认?`,
        okText: '确认',
        okType: 'danger',
        cancelText: '退出',
        onOk() {
          console.log('OK')
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500)
            var user = JSON.parse(localStorage.getItem('user'))
            queryData({ org: user.orgId,user:user.id },'/skillsOption/mieBa').then(res => {
              console.log('同步数据后返回值：', res)
              if (res.code == 0) {
                _this.$message.success('res.message')
                _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            })
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    },
    // 删除
    deleteSkills(record) {
      var _this = this
      this.$confirm({
        title: '警告',
        content: `真的要删除 ${record.title} 吗?`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk() {
          console.log('OK')
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500)
            var id = { entityIds: [record.id] }
            queryData(id, '/skills/delete').then(res => {
              console.log('删除用户返回值：', res)
              if (res.code == 0) {
                _this.$message.success('删除成功')
                _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            })
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    },
    // 批量删除
    deleteSkillsIds() {
      console.log(this.selectedRowKeys)
      var _this = this
      this.$confirm({
        title: '警告',
        content: `真的要删除吗?`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk() {
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500)
            var ids = { entityIds: [_this.selectedRowKeys][0] }

            queryData(ids, '/skills/delete').then(res => {
              if (res.code == 0) {
                _this.$message.success('删除成功')
                _this.selectedRowKeys = []
                _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            })
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    },
    //清空命令集
    delCmd(record) {
      var _this = this
      this.$confirm({
        title: '警告',
        content: ` <${record.title}> 将不再使用 <${record.cmdName}> 命令集`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk() {
          console.log('OK')
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500)
            queryData({ entityId: record.skillsOptionId }, '/skillsOption/deleteCmd').then(res => {
              console.log('删除用户返回值：', res)
              if (res.code == 0) {
                _this.$message.success('删除成功')
                _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            })
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    }
  }
}
