import { STable } from '@/components'
import EditModal from './modules/EditRole'
import CreateModal from './modules/CreateRole'
import { queryData } from '@/api/common'

export default {
  name: 'TableList',
  components: {
    STable,
    EditModal,
    CreateModal
  },
  data() {
    return {
      roleId: 0,
      roleName: '',
      mdl: {},
      // 查询参数
      queryParam: { name: '' },
      // 表头
      columns: [
        {
          title: '唯一识别码',
          dataIndex: 'id'
        },
        {
          title: '角色名称',
          dataIndex: 'name'
        },
        {
          title: '角色等级',
          dataIndex: 'level'
        },
        {
          title: '描述',
          dataIndex: 'describe'
        },
        {
          title: '操作',
          width: '150px',
          dataIndex: 'action',
          scopedSlots: { customRender: 'action' }
        }
      ],

      loadData: parameter => {
        return queryData(Object.assign(parameter, this.queryParam), '/SysRoleManage/querySysRoleMsg').then(res => {
          return res.result
        })
      }
    }
  },
  methods: {
    handleEdit(record) {
      this.mdl = Object.assign({}, record)
      this.mdl.permissions.forEach(permission => {
        permission.actionsOptions = permission.actionEntitySet.map(action => {
          return { label: action.describe, value: action.action, defaultCheck: action.defaultCheck }
        })
      })
      this.roleName = record.name
      console.log('当前选择', record.name)
      this.$refs.editModal.edit(record, record.name)
    },
    handleOk() {
      // 新增/修改 成功时，重载列表
      this.$refs.table.refresh()
    },
    // 处理重置
    handleReset() {
      this.queryParam.name = ''
      this.$refs.table.refresh()
    },
    // 删除
    handleDel(record) {
      var _this = this
      this.$confirm({
        title: '警告',
        content: `真的要删除 ${record.name} 吗?`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk() {
          console.log('OK')
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 300)
            var id = { id: [record.id] }
            queryData(id, '/SysRoleManage/delSysRoleMsg').then(res => {
              if (res.code == 0) {
                _this.$message.success('删除成功')
                _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            })
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    }
  }
}
