import { addData, queryData } from '@/api/common'
import { STable } from '@/components'
import CmdList from '../../Cmd'

export default {
  name: 'TableList',
  components: {
    STable,
    CmdList
  },
  data() {
    return {
      showEditCmd:false,
      labelCol: {
        xs: { span: 24 },
        sm: { span: 7 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 13 }
      },
      visible: false,
      confirmLoading: false,
      cmdList:[],
      editData:[],
      cmd:'',
      form: this.$form.createForm(this),
      showIframe:false,
      mapList:[]
    }
  },
  created(){
    // this.getCmd()
    // this.getMap()
  },
  methods: {
    changeCmd(event) {
      this.editData.cmd = event
      this.showEditCmd = true
    },
    show(record) {
      console.log(record)
      if (record.cmd != '') {
        this.showEditCmd = true
      } else {
        this.showEditCmd = false
      }
      console.log(this.showEditCmd)
      this.editData = record
      this.visible = true
      this.getCmd()
      this.getMap()
    },
    // 修改命令集
    editCmd () {
      console.log('编辑机器人命令集' + this.editData.cmd)
      // if (this.editData.cmd == 0 || this.editData.cmd == '') {
      //   this.$message.error('请先选择要修改的命令集')
      //   return
      // }
      this.$refs.cmd.show('编辑机器人命令集', '', this.editData.cmd)
    },
    // 新增命令集
    addCmd () {
      this.$refs.cmd.show('编辑机器人命令集', '', 0)
    },
    // 编辑命令集后，获取命令集ID
    getCmdId (cmd) {
      this.getCmd()
      this.editData.cmd = cmd
      this.form.setFieldsValue({
        cmd: parseInt(cmd)
      });
    },
    getMap(){
      let org = JSON.parse(localStorage.getItem('user')).orgId;
      queryData({orgId:org},'/robotMapLib/getRobotMapLibList').then(res => {
        console.log('-----------getMap',res);
        this.mapList = res.result;
      })
    },
    getCmd(){
      let org = JSON.parse(localStorage.getItem('user')).orgId;
      let _this = this;
      queryData({orgId:org},'/cmdCollection/getCmdListByOrg').then(res => {
        console.log(res)
        if(res.code == 0){
          _this.cmdList = res.cmdList
        } else {
          _this.$message.error(res.message)
        }
      });
    },
    handleSubmit() {
      const { form: { validateFields } } = this
      this.confirmLoading = true
        validateFields((errors, values) => {
        if (!errors) {
          var _this = this;
          setTimeout(() => {
              let param = {id:this.editData.id,cmd:values.cmd}
            addData(param,'/faceManage/editRobotFace').then(res => {

              if(res.code == 0){
                _this.$message.success('修改成功');
                // 重置输入框内容
                _this.form.resetFields();
                this.visible = false
                this.confirmLoading = false
                this.$emit('ok', values)
              } else {
                _this.$message.error(res.message)
              }
            });

          }, 300)
        } else {
          this.confirmLoading = false
        }
      })
    },
    filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0
    },
  }
}
