import { queryData, addData } from '@/api/common'
import { STable } from '@/components'
export default {
  components: {
    STable
  },
  data() {
    return {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 7 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 13 }
      },
      visible: false,
      confirmLoading: false,
      form: this.$form.createForm(this),
      triggerCmdList: [],
      cmdList: [],
     triggerId:"",
      queryParam:{},
      // 表头
      columns: [
        {
          title: '#',
          dataIndex: 'no',
          scopedSlots: { customRender: 'serial' }
        },

        {
          title: '机器人名称',
          dataIndex: 'name',
          scopedSlots: { customRender: 'name' }
        },
        {
          title: '机器人编码',
          dataIndex: 'code',
          scopedSlots: { customRender: 'code' }
        }
      ],
      // 加载数据方法 必须为 Promise 对象
      loadData: parameter => {
        var id = JSON.parse(localStorage.getItem('user')).id
        console.log('user 请求', parameter)
        var params = {
          type: '1',
          triggerId: this.triggerId,
          code: '',
          name: '',
          orgId: '',
          userId: id,        
          validityDate: ''
        }
        return queryData(
          Object.assign(parameter, params, this.queryParam),
          '/robotInfo/loadPageListByTruggerRobot'
        ).then(res => {
          console.log(res)
          return res.result
        })
      },
      selectedRowKeys: new Set([]),
      selectedRows: [],
      options: {
        alert: {
          show: true
        },
        rowSelection: { 
          onSelect:this.onSelectChange,
          onSelectAll:this.onSelectChangeAll          
        }
      },
      optionAlertShow: false
    }
  },
  created() {
   
  },
  methods: {
   
    handleTriggerCmdChange(value) {
      console.log(`selected ${value}`)
      this.triggerId=value;
        this.$refs.table.refresh()
    },
    filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0
    },
    //选择机器人
    onSelectChange (record, selected, selectedRows, nativeEvent) {     
        console.log(record,selected)  
        if(selected){
            this.selectedRowKeys.add(record.id)           
        }else{
             this.selectedRowKeys.delete(record.id)
        }
   
    },
    onSelectChangeAll(selected, selectedRows, changeRows){    
     if(selected){           
             changeRows.map((value,index)=>{
                 this.selectedRowKeys.add(value.id)   
             })       
        }else{           
             changeRows.map((value,index)=>{
                 this.selectedRowKeys.delete(value.id)   
             })   
        }
    },
    //获取指令列表
    getTriggerCmdList() {
      var _this = this;      
      queryData({}, '/supercmdTrigger/getSupercmdTriggerList').then(res => {
        console.log('指令列表', res)
        if (res.code == 0) {
          _this.triggerCmdList = res.supercmdTriggerList
        } else {
          _this.$message.error(res.message)
        }
      })
    },
 //获取命令集
    getCmdList() {
      var _this = this
       var id = JSON.parse(localStorage.getItem('user')).id
       var param={ "orgId": id}
      queryData(param, '/cmdCollection/getCmdListByOrg').then(res => {
        console.log('命令集', res)
        if (res.code == 0) {
          _this.cmdList = res.cmdList
        } else {
          _this.$message.error(res.message)
        }
      })
    },

    add() {
      console.log("add");
      this.visible = true
      this.getTriggerCmdList()
      this.getCmdList()
      this.selectedRowKeys.clear();    
       if(this.$refs.table){
          this.$refs.table.refresh()
       }     
       
    },

    handleSubmit() {
      const {
        form: { validateFields }
      } = this
      var _this = this
      this.confirmLoading = true
      validateFields((errors, values) => {
        if (!errors) {
          var param = {
            robotId: [..._this.selectedRowKeys],
            cmdId: '1'
          }
          queryData({ ...param, ...values }, '/supercmdTriggerRobot/save').then(res => {
            if (res.code == 0) {
              _this.$message.success('新建成功')
            } else {
              _this.$message.error(res.message)
            }
            _this.visible = false
            _this.confirmLoading = false
            _this.$emit('ok', values)
            // 重置输入框内容
            _this.form.resetFields()
          })
        } else {
          this.confirmLoading = false
        }
      })
    },
    handleCancel() {
      this.visible = false
    }
  }
}
