import { queryData } from '@/api/common'
import CmdList from '../../Cmd'

export default {
  components: {
    CmdList
  },
  data () {
    return {
      showEditCmd: false,
      headers: {
        authorization: 'authorization-text'
      },
      labelCol: {
        xs: { span: 24 },
        sm: { span: 7 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 13 }
      },
      cmdList: [],
      visible: false,
      skillList: [],
      selectSkills: [],
      form: this.$form.createForm(this),
      editData: {},
      isAble: false,
      mapList: []
    }
  },
  created () {

  },
  methods: {
    changeCmd (event) {
      this.editData.cmd = event
      this.showEditCmd = true
    },
    add (record) {
      if (record.cmd != '') {
        this.showEditCmd = true
      } else {
        this.showEditCmd = false
      }
      this.visible = true;
      this.editData = record;
      this.isAble = record.isAble == 1 ? true : false
      this.entityId = record.id
      this.getCmd()
      this.getMap()
    },
    getMap () {
      let org = JSON.parse(localStorage.getItem('user')).orgId;
      queryData({ orgId: org }, '/robotMapLib/getRobotMapLibList').then(res => {
        this.mapList = res.result;
      })
    },
    getCmdId (cmd) {
      this.getCmd();
      this.editData.cmd = cmd
      this.form.setFieldsValue({
      cmd: parseInt(cmd)
      });
    },
    // 新增动作集
    addCmd () {
      let formData = this.form.getFieldsValue();
      let mapId = formData.mapId == undefined ? 0 : formData.mapId
      this.$refs.cmd.show(this.editData.title, mapId, 0)
    },
    // 修改动作集
    editCmd () {
      let formData = this.form.getFieldsValue();
      let mapId = formData.mapId == undefined ? 0 : formData.mapId
      this.$refs.cmd.show(this.editData.title, mapId, this.editData.cmd)
    },
    getCmd(){
      let org = JSON.parse(localStorage.getItem('user')).orgId;
      let _this = this;
      queryData({ orgId: org }, '/cmdCollection/getCmdListByOrg').then(res => {
        if (res.code == 0) {
          _this.cmdList = res.cmdList
        } else {
          _this.$message.error(res.message)
        }
      });
    },
    handleSubmit () {
      const { form: { validateFields } } = this
      this.confirmLoading = true
      validateFields((errors, values) => {
        if (!errors) {
          var _this = this;
          let user = JSON.parse(localStorage.getItem('user'));
          let orgId = user.orgId
          let userId = user.id;
          // let isAble =
          values.isAble = values.isAble == false ? 0 : 1
          var param = { 
            orgId: orgId,
            user: userId,
            entityId: this.editData.id,
            cmd: this.editData.cmd }
          console.log(values);
          queryData(Object.assign(param, values), '/skills/saveOrUpdateXL').then(res => {
            console.log(res)
            if (res.code == 0) {
              _this.$message.success('编辑成功')
              _this.visible = false
              _this.$emit('ok', values)
            } else {
              _this.$message.error(res.message)
            }
          })
        }
      })
    },
    filterOption (input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0
    }
  }
}
