import { queryData } from '@/api/common'
import CmdList from '../../Cmd'
import moment from 'moment'
const week = ['1', '2', '3', '4', '5', '6', '7']
export default {
  components: {
    CmdList
  },
  data () {
    return {
      headers: {
        authorization: 'authorization-text'
      },
      labelCol: {
        xs: { span: 24 },
        sm: { span: 7 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 13 }
      },
      visible: false,
      confirmLoading: false,
      form: this.$form.createForm(this),
      editData: '',
      week,
      showTimePoint: false,
      showWeek: false,
      showTime: false,
      timePoint: '',
      timePointMoment: '',
      time: '',
      timeMoment: '',
      weekList: [], // 日期集合
      cmdList: [], // 命令集集合
      mapList: [], // 地图集合
      showEditCmd: true,
      cmd: ''
    }
  },
  created () {
    this.getCmd()
    this.getMap()
  },
  methods: {
    // 控制显示内容
    display (value) {
      if (value === 1) {
        this.showTime = true
        this.showTimePoint = false
        this.showWeek = false
      }
      if (value === 2) {
        this.showTime = true
        this.showTimePoint = false
        this.showWeek = true
      }
      if (value === 3) {
        this.showTime = true
        this.showTimePoint = true
        this.showWeek = false
      }
    },
    changeCmd (event) {
      this.cmd = event
      this.showEditCmd = true
    },
    getCmdId (cmd) {
      console.log(cmd)
      this.getCmd()
      this.form.setFieldsValue({
        cmd: parseInt(cmd)
      })
    },
    // 修改动作集
    editCmd () {
      const formData = this.form.getFieldsValue()
      const mapId = formData.mapId === undefined ? 0 : formData.mapId
      this.$refs.cmd.show('编辑动作集', mapId, this.cmd)
    },
    // 新增动作集
    addCmd () {
      const formData = this.form.getFieldsValue()
      const mapId = formData.mapId === undefined ? 0 : formData.mapId
      this.$refs.cmd.show('新增动作集', mapId, 0)
    },
    getMap () {
      const org = JSON.parse(localStorage.getItem('user')).orgId
      queryData({ orgId: org }, '/robotMapLib/getRobotMapLibList').then(res => {
        console.log('-----------getMap', res)
        this.mapList = res.result
      })
    },
    getCmd () {
      const org = JSON.parse(localStorage.getItem('user')).orgId
      const _this = this
      queryData({ orgId: org }, '/cmdCollection/getCmdListByOrg').then(res => {
        console.log(res)
        if (res.code === 0) {
          _this.cmdList = res.cmdList
        } else {
          _this.$message.error(res.message)
        }
      })
    },
    timePointStr (dates, dateStrings) {
      this.timePoint = dateStrings
    },
    timeStr (dates, dateStrings) {
      this.time = dateStrings
    },
    // selectWeek (value) {
    //   this.weekList = value
    //   console.log(this.weekList)
    // },
    edit (record) {
      console.log(record)
      this.form.resetFields()
      this.editData = record
      this.cmd = record.cmdId
      this.timeMoment = moment(record.times, 'HH:mm')
      this.time = record.times
      this.visible = true
      if (record.type === 1) {
        this.showTime = true
        this.showTimePoint = false
        this.showWeek = false
      }
      if (record.type === 2) {
        this.showTime = true
        this.showTimePoint = false
        this.showWeek = true
      }
      if (record.type === 3) {
        this.timePointMoment = moment(record.timePoint, 'YYYY-MM-DD')
        this.timePoint = record.timePoint
        this.showTime = true
        this.showTimePoint = true
        this.showWeek = false
      }
    },
    handleSubmit () {
      const { form: { validateFields } } = this
      this.confirmLoading = true
      validateFields((errors, values) => {
        if (!errors) {
          values.time = this.time
          values.timePoint = this.timePoint
          var _this = this
          setTimeout(() => {
            var param = Object.assign(values, { id: this.editData.id })
            queryData(param, '/timedTask/editTaskMsg').then(res => {
              if (res.code === 0) {
                _this.$message.success('修改成功')
              } else {
                _this.$message.error(res.message)
              }
              _this.visible = false
              _this.confirmLoading = false
              _this.$emit('ok', values)
              // 重置输入框内容
              _this.form.resetFields()
            })
          }, 300)
        } else {
          this.confirmLoading = false
        }
      })
    },
    handleCancel () {
      this.visible = false
    }
  }
}
