import { queryData } from '@/api/common'
import moment from 'moment';
export default {
  data () {
    return {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 7 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 13 }
      },
      visible: false,
      confirmLoading: false,
      form: this.$form.createForm(this),
      orgNames:[],
      editData:{},
      orgId:0,
    }
  },
  created(){
    this.handleOrgLists();
  },
  methods: {
    moment,
    // 获取该用户下的所属单位
    handleOrgLists(){
      var _this = this;
      var id = JSON.parse(localStorage.getItem('user')).id;
      var param = { userId : id }
      queryData(param,'/robotInfo/getOrglist').then(res => {
        console.log(res);
        _this.orgNames = res.result;
        if(res.code !== 0){
          _this.$message.error('获取地图',res.message)
        }
          
      }).catch( (err) => console.log(err))
    },
    add (record) { 
      this.orgId = record.orgId;
      this.visible = true;
      this.editData = record;
    },
    handleSubmit () {
      const { form: { validateFields } } = this
      this.confirmLoading = true
      validateFields((errors, values) => {
        if (!errors) {
          values.orgId = Number.isInteger(values.orgId) ? values.orgId : this.orgId;
          var param = Object.assign(values,{entityId:this.editData.id} )
          var _this = this;
          setTimeout(() => {
            queryData(param,'/robotInfo/save').then(res => {
              console.log(res);
              if(res.code == 0){
                _this.$message.success('修改成功');
              } else {
                _this.$message.error(res.message);
              }
               // 重置输入框内容
              _this.form.resetFields();
            });
            this.visible = false
            
            this.confirmLoading = false
            this.$emit('ok', values)
          }, 300)
        } else {
          this.confirmLoading = false
        }
      })
    },
    handleCancel () {
      this.visible = false;
      this.editData = {}
    },
    filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0
    }
  }
}
