import { STable } from '@/components'
import { PageView } from '@/layouts'
import EditForm from './modules/EditPhoneApp'
import CreateForm from './modules/CreatePhoneApp'
import { queryData } from '@/api/common'
export default {
  name: 'TableList',
  components: {
    STable,
    PageView,
    EditForm,
    CreateForm
  },
  data () {
    return {
      headers: {
        authorization: 'authorization-text',
      },
       // 查询参数
      queryParam: {
        appVersion:''
      },
      editData:{}, 
      options:{
          alert: false,
          rowSelection: null
      },
      // 表头
      columns: [
        {
          title: '#',
          dataIndex: 'no',
          scopedSlots: { customRender: 'serial' }
        },
        
        {
          title: '名称',
          dataIndex: 'name',
          scopedSlots: { customRender: 'name' }
        },
        {
          title: '版本号',
          dataIndex: 'appVersion',
          scopedSlots: { customRender: 'appVersion' }
        },
        {
          title: '描述',
          dataIndex: 'contents',
          scopedSlots: { customRender: 'contents' },

        },
        {
          title: '路径',
          dataIndex: 'url',
          scopedSlots: { customRender: 'url' }
        },
        {
          title: '二维码',
          dataIndex: 'QrCodeImage',
          scopedSlots: { customRender: 'QrCodeImage' }
        },
        {
          title: '更新时间',
          dataIndex: 'createDate',
          scopedSlots: { customRender: 'createDate' }
        },
        {
          title: '操作',
          dataIndex: 'action',
          scopedSlots: { customRender: 'action' }
        }
      ],
      // 加载数据方法 必须为 Promise 对象
      loadData: parameter => {
        console.log('App 请求',parameter);
        return queryData(Object.assign(parameter, this.queryParam),'/AppManage/queryAppMsg')
          .then(res => {
            console.log(res);
            return res.result
          })
      },
    }
  },
  computed: {
    title () {
      return this.$route.meta.title
    },
  },
  methods: {
    handleChange(info) {
        if (info.file.status !== 'uploading') {
            console.log(info.file, info.fileList);
        }
        if (info.file.status === 'done') {
            this.$message.success(`${info.file.name} file uploaded successfully`);
        } else if (info.file.status === 'error') {
            this.$message.error(`${info.file.name} file upload failed.`);
        }
    },
    // 处理更新
    handleRefresh(){
      this.$refs.table.refresh()
    },
    // 表单重置
    resetQuery(){
      this.queryParam = {};
      this.$refs.table.refresh(true);
    },
    handleEditVisible(record){
      this.$refs.editModal.add(record);
    },
    // 搜索内容
    searchData(){
      this.$refs.table.refresh(true)
    },
    // 删除
    handleDeleteUser(record){
      var _this = this;
       this.$confirm({
        title: '警告',
        content: `真的要删除 ${record.name} 吗?`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk () {
          console.log('OK')
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500)
            var param = {id: record.id,url:record.url};
            queryData(param,'/AppManage/delRobotAppMsg').then(res => {
              console.log('删除返回值：',res);
              if(res.code == 0){
                 _this.$message.success('删除成功');
                 _this.$refs.table.refresh()
              } else {
                _this.$message.error(res.message)
              }
            });
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel () {
          console.log('Cancel')
        }
      })
      
    },
  }

}
