import { STable } from '@/components'
import { PageView } from '@/layouts'
import CmdList from '../Cmd'
import EditCmdColl from './modules/EditCmdColl'
// import EditRobot from './modules/EditRobot'
import { queryData } from '@/api/common'
import CreateCmdColl from './modules/CreateCmdColl'

export default {
  name: 'TableList',
  components: {
    CreateCmdColl,
    STable,
    PageView,
    EditCmdColl,
    // EditRobot,
    CmdList
  },
  data () {
    return {
      advanced: false,
      addVisible: false,
      editVisible: false,
      // orgId: this.$store.getters.orgId
      orgId: {
        orgId:1
      },
      selectedItems: [],
       // 查询参数
      queryParam: {
        name:'',
        orgId:'',
      },
    //   editData:{},
      cmdpage: {
          alert: false,
          rowSelection: null
      },
      // userId:localStorage.getItem('userId')
      // 表头
      columns: [
        {
          title: '#',
          dataIndex: 'no',
          scopedSlots: { customRender: 'serial' }
        },

        {
          title: '名称',
          dataIndex: 'name',
          scopedSlots: { customRender: 'name' }
        },
        {
          title: '命令集ID',
          dataIndex: 'id',
          scopedSlots: { customRender: 'id' }
        },
        {
          title: '循环次数',
          dataIndex: 'circulation',
          scopedSlots: { customRender: 'circulation' }

        },
        {
          title: '操作时间',
          dataIndex: 'create_date',
          scopedSlots: { customRender: 'create_date' }
        },
        {
          title: '操作',
          dataIndex: 'action',
          scopedSlots: { customRender: 'action' }
        }
      ],
      // 加载数据方法 必须为 Promise 对象
      loadData: parameter => {
        let orgId = JSON.parse(localStorage.getItem('user')).orgId
        return queryData(Object.assign(parameter, this.queryParam,{orgId:orgId}),'/cmdCollection/getCmdCollectionPage')
          .then(res => {
            console.log(res);
            return res.result
          })
      },
      mapList: [],
      roleNames: [],
      selectedRowKeys: [],
    }
  },
  created () {
    this.getMap();
  },
  computed: {
    title () {
      return this.$route.meta.title
    },

  },
  methods: {
    // 获取该单位下的地图
   getMap() {
      let org = JSON.parse(localStorage.getItem('user')).orgId
      queryData({ orgId: org }, '/robotMapLib/getRobotMapLibList').then(res => {
        console.log('-----------getMap', res)
        this.mapList = res.result
      })
    },
    onChange(date, dateString) {
      this.queryParam.validityDate = dateString;
      console.log(date, dateString);
    },
    onSelectChange (selectedRowKeys) {
      this.selectedRowKeys = selectedRowKeys
    },
    // 处理新增
    handleAddSubmit () {
      this.$refs.table.refresh(true)
    },
    // 打开新增命令集界面
    addCmd () {
      this.$refs.CreateCmdColl.add()
    },
    // 表单重置
    resetQuery () {
      this.queryParam = {};
      this.$refs.table.refresh(true)
    },
    // 显示状态修改
    handleAddVisible () {
      this.addVisible = true;
    },
    // 编辑动作集
    handleEditVisible(record) {
      // let mapId = this.editSkills.map == undefined ? 0 : this.editSkills.map
      // let cmd = this.editSkills.cmd == undefined ? 0 : this.editSkills.cmd
      // console.log("编辑动作集"+mapId+"/"+cmd +"/"+ this.businessName +"/"+ this.skillName)
      // this.$refs.cmd.show(this.businessName + this.skillName,mapId,cmd)

      console.info(record)
      // this.editData = record
      // this.$refs.editModal.add(record)
      this.$refs.EditCmdColl.add(record)
    },
    filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0
    },
    // 搜索内容
    searchData(){
      this.$refs.table.refresh(true)
    },
    handleOk(){
      this.$refs.table.refresh(true)
    },
    // 删除命令集
    handleDelete(record){
      var _this = this;
       this.$confirm({
        title: '警告',
        content: `真的要删除 ${record.name} 吗?`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk () {
          console.log('OK')
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500)
            var id = {entityId: record.id};
            queryData(id,'/cmdCollection/delete').then(res => {
              console.log('删除返回值：',res);
              if(res.code == 0){
                 _this.$message.success('删除成功');
                 _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            });
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel () {
          console.log('Cancel')
        }
      })

    },
    // 兼容命令集
    updateCmdPast(record){
      var _this = this;
       this.$confirm({
        title: '警告',
        content: `真的要把 已编辑好的<${record.name}>兼容到现在的版本吗?`,
        okText: '确认',
        okType: 'danger',
        cancelText: '我再想想',
        onOk () {
          console.log('OK')
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 1000)
            var id = {entityId: record.id};
            queryData(id,'/cmdCollection/updateCmdPast').then(res => {
              console.log('删除返回值：',res);
              if(res.code == 0){
                 _this.$message.success('转化成功，请查看');
                 _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            });
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel () {
          console.log('Cancel')
        }
      })

    },
  }

}
