import { STable } from '@/components'
import { PageView } from '@/layouts'
import { queryData } from '@/api/common';
import CreateExpression from './modules/CreateExpression'
import EditExpression from './modules/EditExpression'
export default {
  name: 'TableList',
  components: {
    STable,
    PageView,
    CreateExpression,
    EditExpression
  },
  data () {
    return {
      // 高级搜索 展开/关闭
      advanced: false,
       // 查询参数
      queryParam: {
        name:'',
      },
      // 表头
      columns: [
        {
          title: '#',
          dataIndex: 'no',
          scopedSlots: { customRender: 'serial' }
        },

        {
          title: '编码',
          dataIndex: 'code',
          scopedSlots: { customRender: 'code' }
        },
        {
          title: '名称',
          dataIndex: 'name',
          scopedSlots: { customRender: 'name' }
        },
        {
          title: '操作',
          dataIndex: 'action',
          scopedSlots: { customRender: 'action' }
        }
      ],
      // 加载数据方法 必须为 Promise 对象
      loadData: parameter => {
        console.log('user 请求',parameter);
        return queryData(Object.assign(parameter, this.queryParam),'/robotExpManage/queryRobotExpMsg')
          .then(res => {
            console.log(res);
            return res.result
          })
      },
      selectedRowKeys: [],
      options: {
        alert: { show: true, clear: () => { this.selectedRowKeys = [] } },
        rowSelection: {
          selectedRowKeys: this.selectedRowKeys,
          onChange: this.onSelectChange
        }
      },
      optionAlertShow: false
    }
  },
  computed: {
    title () {
      return this.$route.meta.title
    },
  },
  methods: {
    tableOption () {
      if (!this.optionAlertShow) {
        this.options = {
          alert: { show: true, clear: () => { this.selectedRowKeys = [] } },
          rowSelection: {
            selectedRowKeys: this.selectedRowKeys,
            onChange: this.onSelectChange,
            getCheckboxProps: record => ({
              props: {
                disabled: record.no === 'No 2', // Column configuration not to be checked
                name: record.no
              }
            })
          }
        }
        this.optionAlertShow = true
      } else {
        this.options = {
          alert: false,
          rowSelection: null
        }
        this.optionAlertShow = false
      }
    },
    onSelectChange (selectedRowKeys) {
      this.selectedRowKeys = selectedRowKeys
    },
    // 处理刷新
    handleRefresh(){
      this.$refs.table.refresh(true)
    },
    // 表单重置
    resetQuery(){
      this.queryParam = {};
      this.$refs.table.refresh(true)
    },
    handleEditVisible(record){
      this.$refs.editModal.add(record);
    },
    // 搜索内容
    searchData(){
      this.$refs.table.refresh(true)
    },
    // 删除
    handleDelete(record){
      var _this = this;
       this.$confirm({
        title: '警告',
        content: `真的要删除 ${record.name} 吗?`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk () {
          console.log('OK')
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500)
            var id = {id: [record.id]};
            queryData(id,'/robotExpManage/delRobotExpMsg').then(res => {
              if(res.code == 0){
                 _this.$message.success('删除成功');
                 _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            });
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel () {
          console.log('Cancel')
        }
      })

    },
    // 批量删除
    handleDeletes(){
      console.log(this.selectedRowKeys);
      var _this = this;
       this.$confirm({
        title: '警告',
        content: `真的要删除吗?`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk () {
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500)
            var ids = {id:_this.selectedRowKeys}
            queryData(ids,'/robotExpManage/delRobotExpMsg').then(res => {
              if(res.code == 0){
                 _this.$message.success('删除成功');
                 _this.selectedRowKeys = []
                 _this.$refs.table.refresh()
              } else {
                _this.$message.error(res.message)
              }
            });
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel () {
          console.log('Cancel')
        }
      })
    }
  },

}
