import { getRoleList } from '@/api/manage'
import { getRoleInfo ,editRoleInfo} from '@/api/role'
import { actionToObject } from '@/utils/permissions'
import { mixinDevice } from '@/utils/mixin'
import pick from 'lodash.pick'

export default {
  name: 'RoleModal',
  props:{
    roleName:{
      type:String,
      require:true
    }
  },
  data () {
    return {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 5 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 }
      },
      visible: false,
      confirmLoading: false,
      mdl: {},
      roles:[],
      name:'',
      rname:'',
      form: this.$form.createForm(this),
      permissions: [],
      roleId:0,
    }
  },
  methods: {
    edit (record,name) {
     
      this.roleId = record.id;
      var param = {role:record.id};
      this.rname = name;
      console.log('rnmae:',this.rname);
      getRoleInfo(param).then(res => {
        this.permissions = res.result;
        if(res.code !== 0){
          _this.$message.error(res.message);
        }
        this.mdl = Object.assign({}, record);
        this.visible = true

        // 有权限表，处理勾选
        if (this.mdl.permissions && this.permissions) {
          // 先处理要勾选的权限结构
          const permissionsAction = {}
          this.mdl.permissions.forEach(permission => {
            permissionsAction[permission.permissionId] = permission.actionEntitySet.map(entity => entity.action)
          })
          // 把权限表遍历一遍，设定要勾选的权限 action
          this.permissions.forEach(permission => {
            permission.selected = permissionsAction[permission.name] || []
          })
        }
      }).catch( (err) => console.log(err))

      this.$nextTick(() => {
        this.form.setFieldsValue(pick(this.mdl, 'id', 'name', 'status', 'describe'))
      })
    },
    close () {
      this.$emit('close')
      this.visible = false
    },
    handleSubmit () {

      const _this = this
      // 触发表单验证
      this.form.validateFields((err, values) => {
        // 验证表单没错误
        if (!err) {
          _this.confirmLoading = true
          editRoleInfo({id:_this.roleId,resourceList:_this.permissions,name:values.rname}).then(res => {
            console.log(res);
            if(res.code == 0){
              _this.$message.success('修改成功');
              // 重置输入框内容
              _this.form.resetFields();
              _this.visible = false;
            } else {
              _this.$message.error(res.message);
            }
             _this.confirmLoading = false;
             _this.$emit('ok')
          }).catch((err) => console.log(err))
          
        }
      })
    },
    handleCancel () {
      this.close()
    },
    onChangeCheck (permission) {
      permission.indeterminate = !!permission.selected.length && (permission.selected.length < permission.actionsOptions.length)
      permission.checkedAll = permission.selected.length === permission.actionsOptions.length
    },
    onChangeCheckAll (e, permission) {
      Object.assign(permission, {
        selected: e.target.checked ? permission.actionsOptions.map(obj => obj.value) : [],
        indeterminate: false,
        checkedAll: e.target.checked
      })
    },
