import { STable } from '@/components'
import { queryData } from '@/api/common'

export default {
  name: 'TableList',
  components: {
    STable
  },
  data () {
    return {
      roleId: 0,
      roleName: '',
      mdl: {},
      // 查询参数
      queryParam: { name: '' },
      // 表头
      columns: [
        // {
        //   title: 'id',
        //   dataIndex: 'id'
        // },
        {
          title: '#',
          dataIndex: 'no',
          scopedSlots: { customRender: 'serial' }
        },
        {
          title: '姓名',
          dataIndex: 'name'
        },
        {
          title: '性别',
          dataIndex: 'sex'
        },
        {
          title: '出生年月',
          dataIndex: 'birthday'
        },
        {
          title: '籍贯',
          dataIndex: 'nativePlace'
        },
        {
          title: '民族',
          dataIndex: 'nation'
        },
        {
          title: '身份证号',
          dataIndex: 'idCard'
        },
        {
          title: '住址',
          dataIndex: 'address'
        },
        {
          title: '婚姻状况',
          dataIndex: 'maritalState'
        },
        {
          title: '教育状况',
          dataIndex: 'eduState'
        },
        {
          title: '户籍档案状态',
          dataIndex: 'sts'
        },
        {
          title: '操作',
          dataIndex: 'action',
          scopedSlots: { customRender: 'action' }
        }
      ],

      loadData: parameter => {
        const orgId = JSON.parse(localStorage.getItem('user')).orgId
        return queryData(Object.assign(parameter, this.queryParam, { org: orgId }), '/archives/queryArchives').then(res => {
          return res.result
        })
      }
    }
  },
  methods: {
    handleEdit (record) {
      this.$refs.editModal.edit(record)
    },
    // 挂失
    reportLoss (record) {
      var _this = this
      this.$confirm({
        title: '警告',
        content: `真的要挂失 ${record.name}的户籍档案吗?`,
        okText: '确定',
        okType: 'danger',
        cancelText: '取消',
        onOk () {
          console.log('OK')
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 300)
            var id = { id: record.id }
            queryData(id, '/archives/reportLoss').then(res => {
              if (res.code == 0) {
                _this.$message.success('挂失成功')
                _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            })
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel () {
          console.log('Cancel')
        }
      })
    },
    // 解挂
    relievingLoss (record) {
      var _this = this
      this.$confirm({
        title: '警告',
        content: `真的要解挂 ${record.name}的户籍档案吗?`,
        okText: '确定',
        okType: 'danger',
        cancelText: '取消',
        onOk () {
          console.log('OK')
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 300)
            var id = { id: record.id }
            queryData(id, '/archives/relievingLoss').then(res => {
              if (res.code == 0) {
                _this.$message.success('解挂成功')
                _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            })
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel () {
          console.log('Cancel')
        }
      })
    }
  }
}
